import React, { useRef } from 'react';
import Navbar from './navbar/navbar';
import CardsWrapper from './CardsWrapper';
import { CardProvider, useCard } from './CardContext';
import { PageProvider } from './PageContext';
import Logo from './Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';
import CardRenderer from './CardRenderer';
import NavbarRight from './navbar/navbarright'; // Assurez-vous d'importer correctement

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  z-index: 100000002;
  pointer-events: none;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto;
`;

const CardsContainer = styled.div`
  opacity: 0; /* Initialement à 0 */
  transform: translateY(50px);
  transition: transform 1s ease;
`;

const MainComponentContent = () => {
  const { selectedCard, resetCards } = useCard();
  const [animateLogo, setAnimateLogo] = React.useState(false);
  const [backgroundColor, setBackgroundColor] = React.useState('#FFFFFF'); 
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const cardsRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none';
        }

        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none';
        }

        if (cardsRef.current) {
          gsap.to(cardsRef.current, {
            opacity: 1,  // Passe l'opacité à 1 lors du clic sur "Entrer"
            y: 0,
            duration: 1,
            ease: 'power2.out',
            delay: 0.2,
          });
        }
      },
    });
  };

  const handleXIconClick = () => {
    resetCards(); // Réinitialise la sélection des cartes
    if (cardsRef.current) {
      gsap.set(cardsRef.current, { opacity: 1 }); // Assure que l'opacité est à 1
    }
  };

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <Logo animate={animateLogo} />
      <Navbar />

      {/* Passez la fonction handleXIconClick à NavbarRight */}
      <NavbarRight onResetAnimation={handleXIconClick} />

      {!selectedCard && (
        <CardsContainer ref={cardsRef}>
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}

      <WhiteOverlay ref={overlayRef} />

      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>
    </BackgroundContainer>
  );
};

const MainComponent = () => {
  return (
    <CardProvider>
      <PageProvider>
        <MainComponentContent />
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponentContent;
