import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { FaCar, FaHome } from 'react-icons/fa'; // Utilisation des icônes

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 40px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  background: none;
  border: none;
  font-size: 24px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 32px;
  font-weight: bold;
  color: #000;
  margin-bottom: 20px;
`;

const PlaceGrid = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
`;

const PlaceButton = styled.button`
  background: ${props => props.isSelected ? '#FCEBE5' : '#F8F4F1'};
  border: none;
  border-radius: 16px;
  padding: 20px;
  width: 30%;
  text-align: center;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background: #f0e9e4;
  }
`;

const IconWrapper = styled.div`
  background-color: #000;
  color: #fff;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 10px auto;
`;

const PlaceLabel = styled.div`
  font-size: 16px;
  font-weight: bold;
  color: #000;
`;

const Modal = ({ isOpen, onClose }) => {
  const [selectedPlace, setSelectedPlace] = useState(null);
  const places = [
    { name: "À la maison", icon: <FaHome /> },
    { name: "Dans la voiture", icon: <FaCar /> }
  ];

  const selectPlace = (place) => {
    setSelectedPlace(place);
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Modify your experience</SectionTitle>

        <SectionTitle>Select your place</SectionTitle>
        <PlaceGrid>
          {places.map((place, index) => (
            <PlaceButton
              key={index}
              isSelected={selectedPlace === place.name}
              onClick={() => selectPlace(place.name)}
            >
              <IconWrapper>{place.icon}</IconWrapper>
              <PlaceLabel>{place.name}</PlaceLabel>
            </PlaceButton>
          ))}
        </PlaceGrid>

        {/* Vous pouvez ajouter la sélection des jouets ici */}
      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
