import React, { useState } from 'react';
import styled from 'styled-components';
import Logo from '../images/whitelogo-20.svg'; // Assurez-vous que le chemin est correct

const Background = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #faf3d9;
  z-index: 100000000;
`;

const LogoImage = styled.img`
  position: absolute;
  width: ${({ isAnimating }) => (isAnimating ? '100px' : '300px')};
  top: ${({ isAnimating }) => (isAnimating ? '20px' : '50%')};
  left: ${({ isAnimating }) => (isAnimating ? '20px' : '50%')};
  transform: ${({ isAnimating }) => (isAnimating ? 'translate(0, 0)' : 'translate(-50%, -50%)')};
  transition: all 1s ease;
  opacity: ${({ isAnimating }) => (isAnimating ? 0 : 1)};
`;

const EnterButton = styled.button`
  margin-top: 2rem;
  padding: 1rem 2rem;
  font-size: 2rem;
  background-color: #191919;
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  z-index: 1000;
  position: absolute;
  top: 60%;
  left: 50%;
  transform: translateX(-50%);
  transition: opacity 1s ease;

  &:hover {
    background-color: #333;
  }

  ${({ isAnimating }) => isAnimating && `
    opacity: 0;
    pointer-events: none;
  `}
`;

function LoadingScreen({ onEnter }) {
  const [isAnimating, setIsAnimating] = useState(false);

  const handleClick = () => {
    setIsAnimating(true);
    setTimeout(() => {
      onEnter();
    }, 1000); // Délai pour permettre l'animation de se terminer
  };

  return (
    <Background>
      <LogoImage src={Logo} alt="Logo" isAnimating={isAnimating} />
      <EnterButton onClick={handleClick} isAnimating={isAnimating}>
        Entrer
      </EnterButton>
    </Background>
  );
}

export default LoadingScreen;
