import React, { forwardRef } from 'react';
import styled from 'styled-components';

const AVCardWrapper = styled.div`
  perspective: 1000px;
  width: 392px;
  height: 548px;
  position: absolute;
  cursor: pointer;
  z-index: 5;
`;

const AVCardInner = styled.div`
  width: 100%;
  height: 100%;
  transition: transform 1s;
  transform-style: preserve-3d;
  position: relative;
`;

const AVCardFace = styled.div`
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  position: absolute;
  backface-visibility: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  box-sizing: border-box;
`;

const AVCardFront = styled(AVCardFace)`
  background-image: url(${props => props.image});
`;

const AVCardBack = styled(AVCardFace)`
  background-color: white;
  transform: rotateY(180deg);
  display: flex;
  flex-direction: column;
  padding: 48px;
  justify-content: space-between;
`;

const Title = styled.h1`
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 600;
  margin-bottom: 20px;
`;

const AVCardText = styled.div`
  color: #333;
  font-family: Paragon;
  font-size: 1.75rem;
  text-align: center;
`;

const AVCardComponent = forwardRef(({ onClick, image, title, text, currentPlayer }, ref) => (
  <AVCardWrapper ref={ref} onClick={onClick}>
    <AVCardInner className="inner">
      <AVCardFront image={image} />
      <AVCardBack>
        <Title>{title}</Title>
        <AVCardText>
          {text ? `${currentPlayer}, ${text}` : currentPlayer}
        </AVCardText>
        <div>LoveTogether</div>
      </AVCardBack>
    </AVCardInner>
  </AVCardWrapper>
));

export default AVCardComponent;
