import React, { useRef, useState } from 'react';
import styled from 'styled-components';
import Navbar from './navbar/navbar';
import CardsWrapper from './CardsWrapper';
import { useCard } from './CardContext';
import NavbarRight from './navbar/navbarright';
import CardRenderer from './CardRenderer';
import GrainEffect from './GrainEffect';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const CardsContainer = styled.div`
  opacity: ${({ opacity }) => opacity};
  transform: translateY(${({ isAnimated }) => (isAnimated ? '0' : '50px')});
  transition: ${({ isAnimated }) => (isAnimated ? 'transform 0.8s ease, opacity 0.8s ease' : 'none')};
  pointer-events: ${({ clickable }) => (clickable ? 'auto' : 'none')};
  z-index: 20;
`;

const MainContent = ({ backgroundColor, setBackgroundColor, animateLogo }) => {
  const { selectedCard, resetCards } = useCard();
  const [cardsOpacity, setCardsOpacity] = useState(0);
  const [isAnimated, setIsAnimated] = useState(false);
  const [cardsClickable, setCardsClickable] = useState(false);
  const cardsRef = useRef(null);

  const handleXIconClick = () => {
    resetCards();
    setIsAnimated(false);
    setCardsOpacity(1);
    setCardsClickable(true); // S'assurer que les cartes sont cliquables après le reset
  };

  React.useEffect(() => {
    // Démarrer l'animation des cartes
    if (cardsRef.current) {
      gsap.to(cardsRef.current, {
        opacity: 1,
        y: 0,
        duration: 0.8,
        ease: 'power4.out',
        delay: 0.1,
        onComplete: () => {
          setCardsClickable(true);
        },
      });
    }
  }, []);

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <Navbar />
      <NavbarRight onResetAnimation={handleXIconClick} />

      {!selectedCard && (
        <CardsContainer ref={cardsRef} opacity={cardsOpacity} isAnimated={isAnimated} clickable={cardsClickable}>
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}
      <GrainEffect />
    </BackgroundContainer>
  );
};

export default MainContentContent;
