import React from 'react';
import ActionVerite from './ActionVerite';
import Roleplay from './Roleplay';
import { useCard } from './CardContext';

const TransitionPage = ({ onBack }) => {
  const { selectedCard } = useCard();

  const getNewContent = () => {
    console.log('Getting new content for:', selectedCard);
    switch (selectedCard) {
      case 'card1':
        return <ActionVerite />;
      case 'card2':
        return <div>New content for Card 2</div>;
      case 'card3':
        return <Roleplay />;
      default:
        return null;
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '2em', color: '#333', flexDirection: 'column', backgroundColor: '#fafafa' }}>
      {getNewContent()}
      <button onClick={onBack}>Back</button>
    </div>
  );
};

export default TransitionPage;
