import React, { useRef, useState, useEffect } from 'react';
import styled from 'styled-components';
import Navbar from './navbar/navbar';
import CardsWrapper from './CardsWrapper';
import { useCard } from './CardContext';
import NavbarRight from './navbar/navbarright';
import CardRenderer from './CardRenderer';
import gsap from 'gsap';
import Logo from './Logo'; // Utilisation de Logo ici
import GrainEffect from './GrainEffect';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const LogoContainer = styled.div`
  position: absolute;
  top: ${({ isAnimated }) => (isAnimated ? '50px' : '0')}; /* Position initiale du logo en haut pour l'animation */
  left: 50%;
  transform: translateX(-50%) scale(${({ isAnimated }) => (isAnimated ? '0.2' : '1')}); /* Réduction pendant l'animation */
  transform-origin: center top;
  z-index: 100000001; /* Z-index élevé pour s'assurer qu'il est au-dessus */
  transition: transform 1.5s ease, top 1.5s ease; /* Transition pour l'animation du logo */
  background-color: blue; /* Ajout d'un fond pour visualiser la div */
`;

const CardsContainer = styled.div`
  opacity: ${({ opacity }) => opacity};
  transform: translateY(${({ isAnimated }) => (isAnimated ? '0' : '50px')});
  transition: ${({ isAnimated }) => (isAnimated ? 'transform 0.8s ease, opacity 0.8s ease' : 'none')};
  pointer-events: ${({ clickable }) => (clickable ? 'auto' : 'none')};
  z-index: 20;
`;

const GrainContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto;
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  z-index: 100000002;
  pointer-events: none;
  opacity: 1;
  transition: opacity 1s ease;
`;

const ImageContainer = styled.div`
  position: absolute;
  bottom: 20px;
  left: 20px;
  z-index: 100000; /* Pour s'assurer qu'il est au-dessus des autres éléments */
`;

const StaticLogo = styled.div`
  width: 150px;
  height: auto;
  border: 2px solid black; /* Ajout d'une bordure pour visualiser la div du logo */
`;

const MainContent = ({ backgroundColor, setBackgroundColor }) => {
  const { selectedCard, resetCards } = useCard();
  const [cardsOpacity, setCardsOpacity] = useState(0); // Initial opacity set to 0 for animation
  const [isAnimated, setIsAnimated] = useState(false); // Animation state
  const [cardsClickable, setCardsClickable] = useState(false); // Initial clickability off
  const [animateLogo, setAnimateLogo] = useState(false); // Control for logo animation
  const cardsRef = useRef(null);
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);

  // Ajout de logs pour déboguer les états
  useEffect(() => {
    console.log('isAnimated:', isAnimated);
    console.log('animateLogo:', animateLogo);
  }, [isAnimated, animateLogo]);

  const handleEnter = () => {
    setAnimateLogo(true);
    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none';
        }
        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none';
        }
        setIsAnimated(true);
        setCardsOpacity(1);

        if (cardsRef.current) {
          gsap.to(cardsRef.current, {
            opacity: 1,
            y: 0,
            duration: 0.8,
            ease: 'power4.out',
            delay: 0.1,
            onComplete: () => {
              setCardsClickable(true);
            },
          });
        }
      },
    });
  };

  const handleXIconClick = () => {
    setBackgroundColor('#FFFFFF');
    setTimeout(() => {
      resetCards();
      setIsAnimated(false);
      setCardsOpacity(1);
      setCardsClickable(true);
      if (cardsRef.current) {
        gsap.set(cardsRef.current, { opacity: 1 });
      }
    }, 0);
  };

  useEffect(() => {
    if (!selectedCard) {
      setBackgroundColor('#FFFFFF');
    }
  }, [selectedCard, setBackgroundColor]);

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <GrainContainer>
        <GrainEffect />
      </GrainContainer>

      {/* Test de l'affichage du logo animé */}
      <LogoContainer isAnimated={animateLogo}>
        <Logo />
      </LogoContainer>

      <Navbar />
      <NavbarRight onResetAnimation={handleXIconClick} />

      {!selectedCard && (
        <CardsContainer
          ref={cardsRef}
          opacity={cardsOpacity}
          isAnimated={isAnimated}
          clickable={cardsClickable}
        >
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}

      <WhiteOverlay ref={overlayRef} />
      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>

      {/* Ajout du logo statique ici pour vérification */}
      <ImageContainer>
        <StaticLogo>
          <Logo />
        </StaticLogo>
      </ImageContainer>
    </BackgroundContainer>
  );
};

export default MainContent;
