import React from 'react';
import styled from 'styled-components';
import HomeCard from './HomeCard';

const CardsWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 20px;
  background-color: #8B2514;
  padding: 50px;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #8B2514; /* Couleur par défaut */
  }

  & > div:nth-child(1):hover {
    background-color: #FFD700; /* Couleur pour la première carte */
  }

  & > div:nth-child(2):hover {
    background-color: #FF69B4; /* Couleur pour la deuxième carte */
  }

  & > div:nth-child(3):hover {
    background-color: #FF4500; /* Couleur pour la troisième carte */
  }
`;

const App = () => {
  return (
    <div className="App">
      <CardsWrapper>
        <HomeCard image="/path-to-image/june-baby.png" rotate="-5deg" />
        <HomeCard image="/path-to-image/love-yourself.png" rotate="0deg" />
        <HomeCard image="/path-to-image/love.png" rotate="5deg" />
      </CardsWrapper>
    </div>
  );
};

export default CardsWrapper;
