import React, { useRef, useState, useEffect } from 'react';
import styled from 'styled-components';
import Navbar from './navbar/navbar';
import CardsWrapper from './CardsWrapper';
import { useCard } from './CardContext';
import NavbarRight from './navbar/navbarright';
import CardRenderer from './CardRenderer';
import gsap from 'gsap';
import Logo from './Logo';
import GrainEffect from './GrainEffect';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: #FFFFFF;
`;

const LogoBackground = styled(Logo)`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
  fill: ${({ color }) => color};
`;

const GrainContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 2;
`;

const MainContent = ({ animateLogo }) => {
  const [logoColor, setLogoColor] = useState('#FFFFFF');
  const { selectedCard } = useCard();
  const logoRef = useRef(null);
  const cardsRef = useRef(null);
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);

  const [cardsOpacity, setCardsOpacity] = useState(0);
  const [isAnimated, setIsAnimated] = useState(false);
  const [cardsClickable, setCardsClickable] = useState(false);

  useEffect(() => {
    if (animateLogo) {
      // Animation du logo ici si nécessaire
    }
  }, [animateLogo]);

  const handleEnter = () => {
    // Logique pour l'entrée
  };

  const handleXIconClick = () => {
    // Logique pour le clic sur l'icône X
  };

  useEffect(() => {
    if (!selectedCard) {
      setLogoColor('#FFFFFF');
    }
  }, [selectedCard]);

  return (
    <BackgroundContainer>
      <LogoBackground color={logoColor} />
      <GrainContainer>
        <GrainEffect />
      </GrainContainer>

      <Navbar />
      <NavbarRight onResetAnimation={handleXIconClick} />

      {!selectedCard && (
        <CardsContainer
          ref={cardsRef}
          opacity={cardsOpacity}
          isAnimated={isAnimated}
          clickable={cardsClickable}
        >
          <CardsWrapper setLogoColor={setLogoColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}

      <WhiteOverlay ref={overlayRef} />
      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <Button onClick={handleEnter}>Entrer</Button>
        </ButtonContainer>
      </OverlayContainer>
    </BackgroundContainer>
  );
};

export default MainContent;
