import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 40px;
  right: 40px;
  background: none;
  border: none;
  font-size: 40px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 46px;
  font-weight: bold;
  color: #000;
  margin-bottom: 32px;
  margin-right: 64px;
  line-height: 50px;
`;

const SubTitle = styled.h3`
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 16px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const Input = styled.input`
  padding: 10px;
  margin-bottom: 8px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Textarea = styled.textarea`
  padding: 10px;
  margin-bottom: 8px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
  min-height: 100px;
  resize: vertical;
`;

const Select = styled.select`
  padding: 10px;
  margin-bottom: 8px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: black;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: white;
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;
  width: fit-content;

  &:hover {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const ButtonContainer = styled.div`
  margin-top: 16px;
  display: flex;
  justify-content: flex-end;
  gap: 12px;
`;

const ChipsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 16px;
`;

const Chip = styled.button`
  background: ${props => props.selected ? '#ff4500' : '#f0f0f0'};
  color: ${props => props.selected ? '#fff' : '#000'};
  border: 2px solid ${props => props.selected ? '#ff4500' : '#ddd'};
  border-radius: 20px;
  padding: 8px 16px;
  cursor: pointer;
  font-size: 16px;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;
  flex-shrink: 0;

  &:hover {
    background: #ff4500;
    color: #fff;
  }
`;

const SelectWrapper = styled.div`
  position: relative;
  width: 100%;
`;

const StyledSelect = styled.div`
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 16px;
  cursor: pointer;
  background-color: white;
  display: flex;
  justify-content: space-between;
  align-items: center;

  &:after {
    content: '▼';
    font-size: 12px;
  }
`;

const DropdownList = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  right: 0;
  max-height: 200px;
  overflow-y: auto;
  background-color: white;
  border: 1px solid #ccc;
  border-top: none;
  border-radius: 0 0 4px 4px;
  z-index: 1000;
`;

const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  padding: 8px 12px;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    background-color: #f5f5f5;
  }
`;

const AddTruthOrDareModal = ({ isOpen, onClose }) => {
  // ... (le reste du code du composant reste inchangé)

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Add New Truth or Dare</SectionTitle>
        <Form onSubmit={handleSubmit}>
          <SubTitle>Type</SubTitle>
          <ChipsContainer>
            <Chip
              type="button"
              selected={formData.type === 'truth'}
              onClick={() => handleTypeChange('truth')}
            >
              Truth
            </Chip>
            <Chip
              type="button"
              selected={formData.type === 'dare'}
              onClick={() => handleTypeChange('dare')}
            >
              Dare
            </Chip>
          </ChipsContainer>

          <SubTitle>Player</SubTitle>
          <Select name="player" value={formData.player} onChange={handleChange}>
            <option value="all">Both Players</option>
            <option value="firstName1">Player 1</option>
            <option value="firstName2">Player 2</option>
          </Select>

          <SubTitle>Question or Dare</SubTitle>
          <Textarea
            name="template"
            value={formData.template}
            onChange={handleChange}
            placeholder="Enter the question or dare"
            required
          />

          {formData.type === 'dare' && (
            <>
              <SubTitle>Duration</SubTitle>
              <ChipsContainer>
                {durationOptions.map((option) => (
                  <Chip
                    key={option.value}
                    selected={formData.duration === option.value}
                    onClick={() => handleDurationChange(option.value)}
                  >
                    {option.label}
                  </Chip>
                ))}
              </ChipsContainer>

              <SubTitle>Intensity</SubTitle>
              <ChipsContainer>
                {intensityOptions.map((intensity) => (
                  <Chip
                    key={intensity}
                    selected={formData.intensity.includes(intensity)}
                    onClick={() => handleIntensityChange(intensity)}
                  >
                    {intensity}
                  </Chip>
                ))}
              </ChipsContainer>

              <SubTitle>Toys</SubTitle>
              <SelectWrapper ref={dropdownRef}>
                <StyledSelect onClick={() => setIsDropdownOpen(!isDropdownOpen)}>
                  {formData.toys.length ? `${formData.toys.length} toy(s) selected` : 'Select toys'}
                </StyledSelect>
                {isDropdownOpen && (
                  <DropdownList>
                    <Input
                      type="text"
                      placeholder="Search toys..."
                      value={toySearchTerm}
                      onChange={(e) => setToySearchTerm(e.target.value)}
                      onClick={(e) => e.stopPropagation()}
                    />
                    {filteredToys.map(toy => (
                      <CheckboxLabel key={toy._id}>
                        <input
                          type="checkbox"
                          checked={formData.toys.includes(toy.name_id)}
                          onChange={() => handleToyChange(toy.name_id)}
                        />
                        {toy.name}
                      </CheckboxLabel>
                    ))}
                  </DropdownList>
                )}
              </SelectWrapper>
            </>
          )}

          <ButtonContainer>
            <Button type="submit">Add</Button>
            <Button type="button" onClick={onClose}>Cancel</Button>
          </ButtonContainer>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AddTruthOrDareModal;
