import React from 'react';
import styled from 'styled-components';

const AVButtonContainer = styled.div`
  position: absolute;
  bottom: 48px;
  display: flex;
  gap: 32px;
  margin-top: 20px;
  z-index: 5;
  justify-content: center;
`;

const AVButton = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover:enabled {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.6;
  }
`;

const AVTimerButton = styled(AVButton)`
  background-color: #ff4500;
  color: white;

  &:hover {
    background-color: #ff5733;
  }
`;

const AVSkipButton = styled(AVButton)`
  background-color: #ff5733;
  color: white;

  &:hover {
    background-color: #ff4500;
  }
`;

const AVRecommencerButton = styled(AVTimerButton)``;

const AVTimerRectangle = styled.div`
  position: absolute;
  bottom: 0;
  width: 100%;
  height: 0;
  background-color: rgba(255, 69, 0, 0.5);
  z-index: 2;
`;

const AVTimerAndButtons = ({
  clickedCard,
  duration,
  isStarted,
  isPaused,
  remainingTime,
  showSkipButton,
  currentPlayer,
  intensity,
  handleTimerButtonClick,
  resetAndStartTimer,
  handleSkipClick,
  resetCards,
  changeIntensity,
  formatTime,
}) => (
  <>
    <AVTimerRectangle />

    <AVButtonContainer>
      {!clickedCard && (
        <>
          <div style={{ display: 'flex', flexDirection: 'row', gap: '12px' }}>
            <AVButton disabled>
              <span role="img" aria-label="Tour">ðŸ‘¤</span> Ã€ ton tour, {currentPlayer}
            </AVButton>
            <AVButton onClick={changeIntensity}>
              IntensitÃ© ï½œ {intensity === 'low' ? 'ðŸ‘€  Warm-up' : intensity === 'medium' ? 'ðŸ”¥ Foreplay' : "ðŸ”ž  The Main Event"}
            </AVButton>
          </div>
        </>
      )}

      {clickedCard && (
        <>
          <AVButton onClick={resetCards}>
            <span role="img" aria-label="Tour">ðŸ‘¤</span> Joueur suivant
          </AVButton>

          <div style={{ display: 'flex', flexDirection: 'row', gap: '12px' }}>
            {duration && !isStarted && (
              <AVTimerButton onClick={handleTimerButtonClick}>
                Lancer le timer
              </AVTimerButton>
            )}
            {isStarted && (
              <AVTimerButton onClick={handleTimerButtonClick}>
                {isPaused ? 'Reprendre' : `Pause (${formatTime(remainingTime)})`}
              </AVTimerButton>
            )}
            {duration && isStarted && (
              <AVRecommencerButton onClick={resetAndStartTimer}>
                Recommencer
              </AVRecommencerButton>
            )}
            {showSkipButton && (
              <AVSkipButton onClick={handleSkipClick}>
                Skip
              </AVSkipButton>
            )}
          </div>
        </>
      )}
    </AVButtonContainer>
  </>
);

export default AVTimerAndButtons;
