import React, { useState } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import Logo from './components/Logo';
import styled from 'styled-components';

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000002;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%; 
  pointer-events: all;
`;

const MainComponent = () => {
  const [loading, setLoading] = useState(true);
  const [logoPosition, setLogoPosition] = useState({
    size: '100%',  // Le logo prend toute la largeur de l'écran au début
    top: '50%',    // Positionné verticalement au centre
    left: '50%',   // Positionné horizontalement au centre
  });

  const handleEnter = () => {
    // Redimensionner et repositionner le logo
    setLogoPosition({
      size: '200px',   // Taille après l'animation
      top: '50px',     // Positionné en haut après l'animation
      left: '50%',     // Centré horizontalement
    });

    // Faire disparaître le LoadingScreen après l'animation
    setTimeout(() => {
      setLoading(false);
    }, 1000); // Temps pour l'animation du logo
  };

  return (
    <CardProvider>
      <PageProvider>
        <div>
          {/* Le logo est au-dessus du LoadingScreen */}
          <Logo size={logoPosition.size} top={logoPosition.top} left={logoPosition.left} />
          {loading && (
            <OverlayContainer>
              <ButtonContainer>
                <button onClick={handleEnter}>Entrer</button>
              </ButtonContainer>
            </OverlayContainer>
          )}
          {!loading && (
            <div>
              <Navbar />
              <CardsWrapper />
              <GrainEffect />
            </div>
          )}
        </div>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
