import React, { useState } from 'react';
import LottieAnimation from './LottieAnimation';
import ActionVerite from './ActionVerite';
import Roleplay from './Roleplay';

const TransitionWrapper = ({ selectedCard, onCardSelect }) => {
  const [isLottieVisible, setLottieVisible] = useState(false);
  const [isNewPageVisible, setNewPageVisible] = useState(false);

  const handleCardClick = (card) => {
    onCardSelect(card);
    setLottieVisible(true);
  };

  const handleAnimationComplete = () => {
    setLottieVisible(false);
    setNewPageVisible(true); // Affiche la nouvelle page après l'animation
  };

  const getLottieData = (card) => {
    switch (card) {
      case 'card1':
        return require('../test.json'); // Remplacez par le chemin correct
      case 'card2':
        return require('../test.json'); // Remplacez par le chemin correct
      case 'card3':
        return require('../test.json'); // Remplacez par le chemin correct
      default:
        return null;
    }
  };

  const getNewContent = (card) => {
    switch (card) {
      case 'card1':
        return <ActionVerite />; // Affiche le composant ActionVerite au clic sur la première carte
      case 'card2':
        return <div>New content for Card 2</div>;
      case 'card3':
        return <Roleplay />; // Affiche le composant Roleplay au clic sur la troisième carte
      default:
        return null;
    }
  };

  return (
    <>
      {!isLottieVisible && !isNewPageVisible && (
        <div style={{ display: 'flex', height: '100vh', justifyContent: 'center', alignItems: 'center', gap: '20px', padding: '50px' }}>
          <div onClick={() => handleCardClick('card1')}>
            {/* Vous pouvez insérer un composant pour la carte ici */}
            <div>Card 1</div>
          </div>
          <div onClick={() => handleCardClick('card2')}>
            {/* Vous pouvez insérer un composant pour la carte ici */}
            <div>Card 2</div>
          </div>
          <div onClick={() => handleCardClick('card3')}>
            {/* Vous pouvez insérer un composant pour la carte ici */}
            <div>Card 3</div>
          </div>
        </div>
      )}

      {isLottieVisible && (
        <LottieAnimation
          animationData={getLottieData(selectedCard)}
          onComplete={handleAnimationComplete}
        />
      )}

      {!isLottieVisible && isNewPageVisible && (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '2em', color: '#333', flexDirection: 'column' }}>
          {getNewContent(selectedCard)}
        </div>
      )}
    </>
  );
};

export default TransitionWrapper;
