import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10000000000000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 40px;
  right: 40px;
  background: none;
  border: none;
  font-size: 40px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 46px;
  font-weight: bold;
  color: #000;
  margin-bottom: 20px;
`;

const SubTitle = styled.h3`
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 16px;
`;

const ToyGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const ToyButton = styled.button`
  background: ${props => props.isSelected ? '#ff4500' : '#fff'};
  border: 1px solid ${props => props.isSelected ? '#ff4500' : '#ddd'};
  color: ${props => props.isSelected ? '#fff' : '#000'};
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;

  &:hover {
    background: #ff4500;
    color: #fff;
    border-color: #ff4500;
  }
`;

const PlaceGrid = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 20px;
`;

const PlaceCard = styled.div`
  background: #F9F5F3;
  border-radius: 12px;
  padding: 20px;
  width: 200px;
  height: 150px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  border: ${props => props.isSelected ? '2px solid #ff4500' : '2px solid transparent'};
  transition: background-color 0.3s ease, border-color 0.3s ease;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const ColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const SmallColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const InputField = styled.input`
  padding: 10px;
  margin-bottom: 20px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Modal = ({ isOpen, onClose, onSave, selectedToys, selectedPlace, formData, setFormData }) => {
  const [toys, setToys] = useState([]);
  const [selectedToysState, setSelectedToysState] = useState(selectedToys || []);
  const [places] = useState([
    { name: 'At home', icon: '🏠' },
    { name: 'In the car', icon: '🚗' }
  ]);
  const [selectedPlaceState, setSelectedPlaceState] = useState(selectedPlace || null);

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await axios.get('http://localhost:1812/api/toys');
        setToys(response.data);
      } catch (error) {
        console.error('Erreur lors du chargement des toys:', error);
      }
    };

    fetchToys();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const toggleToySelection = (toy) => {
    setSelectedToysState(prevSelectedToys =>
      prevSelectedToys.includes(toy)
        ? prevSelectedToys.filter(t => t !== toy)
        : [...prevSelectedToys, toy]
    );
  };

  const selectPlace = (place) => {
    setSelectedPlaceState(place);
  };

  const handleSave = () => {
    onSave({ ...formData, selectedToys: selectedToysState, selectedPlace: selectedPlaceState });
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Modify your experience</SectionTitle>
        <ColumnContainer>
          <InputField
            type="text"
            name="firstName1"
            placeholder="First Name 1"
            value={formData.firstName1 || ''}
            onChange={handleChange}
          />
          <InputField
            type="text"
            name="firstName2"
            placeholder="First Name 2"
            value={formData.firstName2 || ''}
            onChange={handleChange}
          />
          <SmallColumnContainer>
            <SubTitle>Select your place</SubTitle>
            <PlaceGrid>
              {places.map((place, index) => (
                <PlaceCard
                  key={index}
                  isSelected={selectedPlaceState === place.name}
                  onClick={() => selectPlace(place.name)}
                >
                  <div style={{ fontSize: '36px' }}>{place.icon}</div>
                  <div>{place.name}</div>
                </PlaceCard>
              ))}
            </PlaceGrid>
          </SmallColumnContainer>
          <SmallColumnContainer>
            <SubTitle>Select your toys</SubTitle>
            <ToyGrid>
              {toys.map((toy, index) => (
                <ToyButton
                  key={index}
                  isSelected={selectedToysState.includes(toy.name)}
                  onClick={() => toggleToySelection(toy.name)}
                >
                  {toy.name}
                </ToyButton>
              ))}
            </ToyGrid>
          </SmallColumnContainer>
        </ColumnContainer>
        <button onClick={handleSave}>Save</button>
      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
