import React, { useState } from 'react';
import styled from 'styled-components';

const CardsBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #faf3d9;
  z-index: 100000000;
`;

const Card = styled.div`
  width: 339px;
  height: 474px;
  background-color: ${({ color }) => color};
  position: absolute;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.1);
  border-radius:12px;
  ${({ index }) =>
    index === 0 && `top: -50px; left: -100px; transform: rotate(15deg);`}   /* Ok */
  ${({ index }) =>
    index === 1 && `top: -220px; left: 180px; transform: rotate(-90deg);`} /* Ok */
  ${({ index }) =>
    index === 2 && `top: -240px; right: 300px; transform: rotate(-75deg);`} /* Ok */
  ${({ index }) =>
    index === 3 && `top: -240px; right: -100px; transform: rotate(-15deg);`}
  ${({ index }) =>
    index === 4 && `top: 200px; right: -120px; transform: rotate(10deg);`}
  ${({ index }) =>
    index === 5 && `bottom: 200px; right: -150px; transform: rotate(-5deg);`}
  ${({ index }) =>
    index === 6 && `bottom: 80px; right: -100px; transform: rotate(5deg);`}
  ${({ index }) =>
    index === 7 && `bottom: -40px; right: 180px; transform: rotate(-20deg);`}
  ${({ index }) =>
    index === 8 && `bottom: -80px; left: 180px; transform: rotate(10deg);`}
  ${({ index }) =>
    index === 9 && `bottom: -50px; left: -100px; transform: rotate(-10deg);`}
  ${({ index }) =>
    index === 10 && `bottom: 80px; left: -120px; transform: rotate(20deg);`}
  ${({ index }) =>
    index === 11 && `bottom: -200px; left: 400px; transform: rotate(74deg);`} /* Ok */
  ${({ index }) =>
    index === 12 && `top: 200px; left: -120px; transform: rotate(5deg);`}
  ${({ index }) =>
    index === 13 && `top: -200px; transform: rotate(-90deg);`} /* Ok */
  ${({ index }) =>
    index === 14 && `top: 100px; right: 100px; transform: rotate(15deg);`}
  ${({ index }) =>
    index === 15 && `bottom: -250px; right: 400px; transform: rotate(78deg);`}
  ${({ index }) =>
    index === 16 && `top: 100px; left: -150px; transform: rotate(10deg);`}
  ${({ index }) =>
    index === 17 && `bottom: -300px; transform: rotate(-78deg);`} /* Ok */
`;

const Content = styled.div`
  position: absolute;
  z-index: 10;
  text-align: center;
  max-width: 800px;
`;

const Title = styled.h1`
  font-size: 6rem;
  line-height:6rem;
  text-align:center;
  margin: 0;
  color: #191919;
  opacity: 0;
  color: #000;
  font-family: Paragon;
  animation: fadeIn 2s forwards;
`;

const Subtitle = styled.h2`
  font-size: 6rem;
  margin-top: 0.5rem;
  color: #191919;
  opacity: 0;
  font-family: "Montserrat";
  font-weight: 800;
  line-height: 97.05%;
  letter-spacing: -1.368px;
  animation: fadeIn 3s forwards;
`;

const EnterButton = styled.button`
  margin-top: 3rem;
  padding: 1rem 2rem;
  font-size: 2rem;
  background-color: #191919;
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  animation: fadeIn 4s forwards;

  &:hover {
    background-color: #333;
  }
`;

const fadeInKeyframes = `
  @keyframes fadeIn {
    to {
      opacity: 1;
    }
  }
`;

const colors = [
  '#d7f07d', '#fcba83', '#5ed5fb', '#f979b4', '#fc83d7', '#abffb0',
  '#f8c6f2', '#83fcba', '#fcd783', '#83a9fc', '#f783fc', '#f8fc83',
  '#a9fcb0', '#fcf3a9', '#b0a9fc', '#83fca9', '#fc83a9', '#fca983'
];

function LoadingScreen({ onEnter }) {
  return (
    <>
      <style>{fadeInKeyframes}</style>
      <CardsBackground>
        {Array.from({ length: 18 }).map((_, index) => (
          <Card key={index} index={index} color={colors[index % colors.length]} />
        ))}
        <Content>
          <Title>A NIGHT OF CONVERSATION</Title>
          <Subtitle>EXPLORING THE CONFLICTS IN DESIGN</Subtitle>
          <EnterButton onClick={onEnter}>Entrer</EnterButton>
        </Content>
      </CardsBackground>
    </>
  );
}

export default LoadingScreen;
