import React from 'react';
import HomeCard from './HomeCard';
import { useCard } from './CardContext'; // Assurez-vous que c'est importé
import ActionVerite from './ActionVerite';

const CardsWrapper = ({ setBackgroundColor }) => {
  const { selectCard } = useCard(); // Utilisez selectCard pour mettre à jour l'état
  const defaultBackgroundColor = '#FBF8F1';

  const handleCardClick = (card) => {
    selectCard(card); // Met à jour l'état selectedCard
  };

  const handleMouseEnter = (color) => {
    setBackgroundColor(color);
  };

  const handleMouseLeave = () => {
    setBackgroundColor(defaultBackgroundColor);
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row',
      width: '100vw',
      height: '100vh',
      justifyContent: 'center',
      alignItems: 'center',
      gap: '20px',
      padding: '50px'
    }}>
      <HomeCard
        image={require('../images/love.png')}
        rotate="-5deg"
        onMouseEnter={() => handleMouseEnter('#FFD700')}
        onMouseLeave={handleMouseLeave}
        onClick={() => handleCardClick('actionVerite')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(-5deg)',
          backgroundColor: defaultBackgroundColor,
        }}
      />
      <HomeCard
        image={require('../images/love.png')}
        rotate="0deg"
        onMouseEnter={() => handleMouseEnter('#FF69B4')}
        onMouseLeave={handleMouseLeave}
        onClick={() => handleCardClick('generator')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(0deg)',
          backgroundColor: defaultBackgroundColor,
        }}
      />
      <HomeCard
        image={require('../images/love.png')}
        rotate="5deg"
        onMouseEnter={() => handleMouseEnter('#FF4500')}
        onMouseLeave={handleMouseLeave}
        onClick={() => handleCardClick('roleplay')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(5deg)',
          backgroundColor: defaultBackgroundColor,
        }}
      />
    </div>
  );
};

export default CardsWrapper;
