// src/components/AVModalForeplay.js
import React from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const ModalButton = styled.button`
  padding: 10px 20px;
  background-color: #ff4500;
  border: none;
  border-radius: 12px;
  color: white;
  font-size: 16px;
  margin: 10px;
  cursor: pointer;

  &:hover {
    background-color: #ff5733;
  }
`;

const AVModalForeplay = ({ dizaine, onAccept, onDecline }) => {
  return (
    <ModalOverlay>
      <ModalContent>
        <h2>Passer à The Main Event ?</h2>
        <p>
          Vous avez cliqué {dizaine * 10} fois au total sur une carte Dare en Foreplay.
          Voulez-vous passer à The Main Event ?
        </p>
        <div>
          <ModalButton onClick={onAccept}>Oui</ModalButton>
          <ModalButton onClick={onDecline}>Non</ModalButton>
        </div>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AVModalForeplay;
