import React, { useState, useEffect } from 'react';
import LottieAnimation from './LottieAnimation';
import ActionVerite from './ActionVerite';
import Roleplay from './Roleplay';
import { useCard } from './CardContext';

const TransitionPage = ({ onComplete, onBack }) => {
  const { selectedCard, isLottieVisible, finishAnimation } = useCard();
  const [showContent, setShowContent] = useState(false);

  // Utilisation de useEffect pour gérer la fin de l'animation et le délai
  useEffect(() => {
    if (!isLottieVisible) {
      const timer = setTimeout(() => {
        setShowContent(true);
      }, 1000); // Délai de 1 seconde après l'animation

      return () => clearTimeout(timer); // Nettoie le timer si le composant est démonté
    }
  }, [isLottieVisible]);

  const getLottieData = () => {
    switch (selectedCard) {
      case 'card1':
        return require('../test.json'); // Chemin correct vers le fichier Lottie
      case 'card2':
        return require('../test.json'); // Chemin correct vers le fichier Lottie
      case 'card3':
        return require('../test.json'); // Chemin correct vers le fichier Lottie
      default:
        return null;
    }
  };

  const getNewContent = () => {
    if (!showContent) return null;

    switch (selectedCard) {
      case 'card1':
        return <ActionVerite />;
      case 'card2':
        return <div>New content for Card 2</div>;
      case 'card3':
        return <Roleplay />;
      default:
        return null;
    }
  };

  return (
    <div>
      {isLottieVisible && (
        <LottieAnimation
          animationData={getLottieData()}
          onComplete={finishAnimation}
        />
      )}
      {showContent && (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '2em', color: '#333', flexDirection: 'column' }}>
          {getNewContent()}
          <button onClick={onBack}>Back</button>
        </div>
      )}
    </div>
  );
};

export default TransitionPage;
