import React, { useEffect } from 'react';
import HomeCard from './HomeCard';
import { useCard } from './CardContext';
import LottieAnimation from './LottieAnimation';  // Assurez-vous que l'import est correct

const CardsWrapper = ({ setBackgroundColor }) => {
  const { selectCard, isLottieVisible, finishAnimation } = useCard();

  const handleCardClick = (card) => {
    console.log('Card clicked:', card);
    selectCard(card);  // Déclenche l'animation Lottie et la transition de page
  };

  useEffect(() => {
    console.log('Lottie visibility:', isLottieVisible);
  }, [isLottieVisible]);

  if (isLottieVisible) {
    return (
      <LottieAnimation onComplete={finishAnimation} />
    );
  }

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row',
      width: '100vw',
      height: '100vh',
      justifyContent: 'center',
      alignItems: 'center',
      gap: '20px',  // Espace entre les cartes
      padding: '50px'  // Pour centrer les cartes
    }}>
      <HomeCard
        image={require('../images/junebaby.png')}
        rotate="-5deg"
        onMouseEnter={() => setBackgroundColor('#FFD700')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
        onClick={() => {
          handleCardClick('card1');
          setBackgroundColor('#FBF8F1');
        }}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(-5deg)',  // Appliquer la rotation
          backgroundColor: '#FBF8F1',  // Fond par défaut
        }}
      />
      <HomeCard
        image={require('../images/yourself.png')}
        rotate="0deg"
        onMouseEnter={() => setBackgroundColor('#FF69B4')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
        onClick={() => handleCardClick('card2')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(0deg)',  // Appliquer la rotation
          backgroundColor: '#FBF8F1',  // Fond par défaut
        }}
      />
      <HomeCard
        image={require('../images/love.png')}
        rotate="5deg"
        onMouseEnter={() => setBackgroundColor('#FF4500')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
        onClick={() => handleCardClick('card3')}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(5deg)',  // Appliquer la rotation
          backgroundColor: '#FBF8F1',  // Fond par défaut
        }}
      />
    </div>
  );
};

export default CardsWrapper;
