import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;
  width: fit-content;

  &:hover {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const ButtonContainer = styled.div`
  margin-top: 16px;
  display: flex;
  flex-direction: column;
  align-items: end;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 40px;
  right: 40px;
  background: none;
  border: none;
  font-size: 40px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 46px;
  font-weight: bold;
  color: #000;
  margin-bottom: 32px;
  margin-right: 64px;
  line-height: 50px;
`;

const SubTitle = styled.h3`
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 16px;
`;

const ToyGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const ToyButton = styled.button`
  background: ${props => props.isSelected ? '#ff4500' : '#fff'};
  border: 1px solid ${props => props.isSelected ? '#ff4500' : '#ddd'};
  color: ${props => props.isSelected ? '#fff' : '#000'};
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;

  &:hover {
    background: #ff4500;
    color: #fff;
    border-color: #ff4500;
  }
`;

const ColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const SmallColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0px;
`;

const InputField = styled.input`
  padding: 10px;
  margin-bottom: 8px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Modal = ({ isOpen, onClose, onSave, selectedToys, formData, setFormData }) => {
  const [toys, setToys] = useState([]);
  const [selectedToysState, setSelectedToysState] = useState(selectedToys || []);

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await fetch('http://localhost:1812/api/toys');
        const data = await response.json();
        setToys(data);
      } catch (error) {
        console.error('Error fetching toys:', error);
      }
    };

    fetchToys();

    if (isOpen) {
      const savedPreferences = localStorage.getItem('userPreferences');
      if (savedPreferences) {
        const savedData = JSON.parse(savedPreferences);
        setFormData({
          firstName1: savedData.firstName1 || '',
          firstName2: savedData.firstName2 || '',
        });
        setSelectedToysState(savedData.selectedToys || []);
      }
    }
  }, [isOpen, setFormData]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const toggleToySelection = (toy) => {
    setSelectedToysState(prevSelectedToys =>
      prevSelectedToys.includes(toy)
        ? prevSelectedToys.filter(t => t !== toy)
        : [...prevSelectedToys, toy]
    );
  };

  const handleSave = () => {
    const dataToSave = { ...formData, selectedToys: selectedToysState };
    localStorage.setItem('userPreferences', JSON.stringify(dataToSave));
    onSave(dataToSave);
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Personnaliser votre expÃ©rience</SectionTitle>
        <ColumnContainer>
          <SmallColumnContainer>
            <SubTitle>Ã€ qui avons nous l'honneur ?</SubTitle>
            <InputField
              type="text"
              name="firstName1"
              placeholder="Femme"
              value={formData.firstName1 || ''}
              onChange={handleChange}
            />
            <InputField
              type="text"
              name="firstName2"
              placeholder="Homme"
              value={formData.firstName2 || ''}
              onChange={handleChange}
            />
          </SmallColumnContainer>
          <SmallColumnContainer>
            <SubTitle>Qu'est ce qu'il y'a au menu ?</SubTitle>
            <ToyGrid>
              {toys.map((toy, index) => (
                <ToyButton
                  key={index}
                  isSelected={selectedToysState.includes(toy.name)}
                  onClick={() => toggleToySelection(toy.name)}
                >
                  {toy.name}
                </ToyButton>
              ))}
            </ToyGrid>
          </SmallColumnContainer>
        </ColumnContainer>

        <ButtonContainer>
          <Button onClick={handleSave}>
            <span role="img" aria-label="Tour">ðŸ‘¤</span> Confirmer 
          </Button>
        </ButtonContainer>

      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
