import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  max-height: 95vh;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
  overflow-y: auto;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  background: none;
  border: none;
  font-size: 24px;
  cursor: pointer;
  color: #000;
`;

const Title = styled.h2`
  font-size: 32px;
  font-weight: bold;
  color: #000;
  margin-bottom: 32px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 24px;
`;

const ChipsContainer = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
`;

const Chip = styled.button`
  background: ${props => props.selected ? '#FF69B4' : '#f0f0f0'};
  color: ${props => props.selected ? '#fff' : '#000'};
  border: none;
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 16px;
  transition: background-color 0.3s ease, color 0.3s ease;

  &:hover {
    background: ${props => props.selected ? '#FF69B4' : '#e0e0e0'};
  }
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  border: 1px solid #ccc;
  border-radius: 8px;
  font-size: 16px;
`;

const Label = styled.label`
  font-size: 18px;
  font-weight: bold;
  margin-bottom: 8px;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 10px;
`;

const Button = styled.button`
  background: ${props => props.primary ? '#FF69B4' : '#f0f0f0'};
  color: ${props => props.primary ? '#fff' : '#000'};
  border: none;
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 16px;
  transition: background-color 0.3s ease, color 0.3s ease;

  &:hover {
    background: ${props => props.primary ? '#FF1493' : '#e0e0e0'};
  }
`;
const ModalLayout = styled.div`
  display: flex;
  gap: 40px;
`;

const FormSection = styled.div`
  flex: 1;
`;

const CardSection = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Card = styled.div`
  width: 392px;
  height: 548px;
  background-color: #FBF8F1;
  border-radius: 16px;
  padding: 48px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
`;

const CardTitle = styled.h3`
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 600;
  text-transform: uppercase;
  margin-bottom: 20px;
`;

const CardContent = styled.p`
  font-family: Paragon, serif;
  font-size: 24px;
  text-align: center;
  flex-grow: 1;
  display: flex;
  align-items: center;
`;

const CardFooter = styled.div`
  font-family: 'Poppins', sans-serif;
  text-transform: uppercase;
  font-size: 14px;
  font-weight: 400;
`;

const AddTruthOrDareModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    type: 'dare',
    player: 'all',
    template: '',
    intensity: 'Warm-Up',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleTypeChange = (type) => {
    setFormData({ ...formData, type });
  };

  const handlePlayerChange = (player) => {
    setFormData({ ...formData, player });
  };

  const handleIntensityChange = (intensity) => {
    setFormData({ ...formData, intensity });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:1812/api/truthordare', formData);
      alert('Truth or Dare added successfully!');
      onClose();
    } catch (error) {
      console.error('Error adding Truth or Dare:', error);
      alert('Failed to add Truth or Dare. Please try again.');
    }
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <Title>Ajouter un dare</Title>
        <ModalLayout>
          <FormSection>
            <Form onSubmit={handleSubmit}>
              <ChipsContainer>
                <Chip
                  type="button"
                  selected={formData.type === 'truth'}
                  onClick={() => handleTypeChange('truth')}
                >
                  Truth 👀
                </Chip>
                <Chip
                  type="button"
                  selected={formData.type === 'dare'}
                  onClick={() => handleTypeChange('dare')}
                >
                  Dare 🔥
                </Chip>
              </ChipsContainer>

              <Label>Gage</Label>
              <Input
                name="template"
                value={formData.template}
                onChange={handleChange}
                placeholder="Joueur 1, vous allez devoir vous occuper de Clémence en lui faisant un cuni."
                required
              />

              <Label>Qui peut participer au gage ?</Label>
              <ButtonGroup>
                <Button type="button" primary={formData.player === 'all'} onClick={() => handlePlayerChange('all')}>Les deux</Button>
                <Button type="button" primary={formData.player === 'firstName2'} onClick={() => handlePlayerChange('firstName2')}>Homme</Button>
                <Button type="button" primary={formData.player === 'firstName1'} onClick={() => handlePlayerChange('firstName1')}>Femme</Button>
              </ButtonGroup>

              <Label>Intensité ?</Label>
              <ButtonGroup>
                <Button type="button" primary={formData.intensity === 'Warm-Up'} onClick={() => handleIntensityChange('Warm-Up')}>Warm-Up</Button>
                <Button type="button" primary={formData.intensity === 'Foreplay'} onClick={() => handleIntensityChange('Foreplay')}>Foreplay</Button>
                <Button type="button" primary={formData.intensity === 'MainEvent'} onClick={() => handleIntensityChange('MainEvent')}>Main Event</Button>
              </ButtonGroup>

              <Button type="submit" primary>Confirmer</Button>
            </Form>
          </FormSection>
          <CardSection>
            <Card>
              <CardTitle>TRUTH OR DARE</CardTitle>
              <CardContent>
                {formData.template || "Le contenu du gage apparaîtra ici"}
              </CardContent>
              <CardFooter>LOVETOGETHER</CardFooter>
            </Card>
          </CardSection>
        </ModalLayout>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AddTruthOrDareModal;
