import React, { useState, useEffect } from 'react';
import LottieAnimation from './LottieAnimation';
import ActionVerite from './ActionVerite';  // Assurez-vous que ces composants sont bien importés
import Roleplay from './Roleplay';
import { useCard } from './CardContext';

const TransitionPage = () => {
  const { selectedCard, isLottieVisible, finishAnimation } = useCard();
  const [showNewContent, setShowNewContent] = useState(false);

  useEffect(() => {
    if (!isLottieVisible && selectedCard) {
      const timer = setTimeout(() => {
        setShowNewContent(true);
      }, 1000); // Délai de 1 seconde après la fin de l'animation

      return () => clearTimeout(timer);
    }
  }, [isLottieVisible, selectedCard]);

  const getLottieData = () => {
    // Utilisation du même fichier Lottie pour toutes les cartes
    return require('../test.json');
  };

  const getNewContent = () => {
    if (!showNewContent) return null;

    switch (selectedCard) {
      case 'card1':
        return <ActionVerite />;
      case 'card2':
        return <div>New content for Card 2</div>;  // Vous pouvez remplacer ce placeholder par un autre composant si nécessaire
      case 'card3':
        return <Roleplay />;
      default:
        return null;
    }
  };

  return (
    <div>
      {isLottieVisible && (
        <LottieAnimation
          animationData={getLottieData()}
          onComplete={finishAnimation}
        />
      )}
      {!isLottieVisible && showNewContent && (
        <div style={{ width: '100vw', height: '100vh' }}>
          {getNewContent()}
        </div>
      )}
    </div>
  );
};

export default TransitionPage;
