const MainComponentContent = () => {
  const { selectedCard, resetCard } = useCard(); // Utilise resetCard pour réinitialiser l'affichage des cartes
  const [animateLogo, setAnimateLogo] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState('#FFFFFF'); // Couleur de fond par défaut
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const cardsRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    // Utiliser GSAP pour animer la disparition du fond blanc et du bouton simultanément
    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none'; // Désactive les interactions
        }

        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none'; // Désactive les interactions du bouton après l'animation
        }

        // Animation des cartes
        if (cardsRef.current) {
          gsap.to(cardsRef.current, {
            opacity: 1,
            y: 0,
            duration: 1,
            ease: 'power2.out',
            delay: 0.2,
          });
        }
      }
    });
  };

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <Logo animate={animateLogo} />
      <Navbar />

      {selectedCard ? (
        <ActionVerite card={selectedCard} />
      ) : (
        <CardsContainer ref={cardsRef}>
          <CardsWrapper setBackgroundColor={setBackgroundColor} /> {/* Passer la fonction au composant */}
        </CardsContainer>
      )}

      <WhiteOverlay ref={overlayRef} />

      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>
    </BackgroundContainer>
  );
};
