import React, { useState } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const FullScreenCover = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #77ffee;
  z-index: 9999;
  pointer-events: none;
  transform-origin: left center;
  transform: scaleX(0); /* Start off-screen */
`;

const CardsWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 20px;
  padding: 50px;
  height: 100vh;
`;

const Card = styled.div`
  width: 300px;
  height: 450px;
  background-color: #f0f0f0;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  cursor: pointer;
`;

const App = () => {
  const [isAnimating, setIsAnimating] = useState(false);

  const handleClick = () => {
    if (!isAnimating) {
      setIsAnimating(true);
      gsap.to('.cover', {
        scaleX: 1,
        duration: 1.5,
        ease: 'power4.inOut',
        onComplete: () => {
          // Transition to the next page or content here
          console.log('Animation completed');
        }
      });
    }
  };

  return (
    <>
      <FullScreenCover className="cover" />
      <CardsWrapper>
        <Card onClick={handleClick} />
        <Card onClick={handleClick} />
        <Card onClick={handleClick} />
      </CardsWrapper>
    </>
  );
};

export default App;
