import React, { useState } from 'react';
import styled from 'styled-components';
import Logo from '../images/whitelogo-20.svg';

const Background = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #faf3d9;
  z-index: 100000000;
`;

const LogoImage = styled.img`
  width: ${({ isAnimating }) => (isAnimating ? '100px' : '300px')};
  transition: all 1s ease;
  position: ${({ isAnimating }) => (isAnimating ? 'absolute' : 'relative')};
  top: ${({ isAnimating }) => (isAnimating ? '20px' : 'auto')};
  left: ${({ isAnimating }) => (isAnimating ? '20px' : 'auto')};
`;

const EnterButton = styled.button`
  margin-top: 2rem;
  padding: 1rem 2rem;
  font-size: 2rem;
  background-color: #191919;
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  z-index: 1000;

  &:hover {
    background-color: #333;
  }
`;

function LoadingScreen({ onEnter }) {
  const [isAnimating, setIsAnimating] = useState(false);

  const handleClick = () => {
    setIsAnimating(true);
    setTimeout(() => {
      onEnter();
    }, 1000); // Délai pour permettre l'animation de se terminer
  };

  return (
    <Background>
      <LogoImage src={Logo} alt="Logo" isAnimating={isAnimating} />
      {!isAnimating && <EnterButton onClick={handleClick}>Entrer</EnterButton>}
    </Background>
  );
}

export default LoadingScreen;
