import React, { useRef, useState } from 'react';
import { useLocation } from 'react-router-dom';
import Navbar from './navbar/navbar';
import CardsWrapper from './CardsWrapper';
import { useCard } from './CardContext';
import Logo from './Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';
import CardRenderer from './CardRenderer';
import NavbarRight from './navbar/navbarright';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  z-index: 100000002;
  pointer-events: none;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto;
`;

const CardsContainer = styled.div`
  opacity: ${({ opacity }) => opacity};
  transform: translateY(${({ isAnimated }) => (isAnimated ? '0' : '50px')});
  transition: ${({ isAnimated }) => (isAnimated ? 'transform 0.8s ease, opacity 0.8s ease' : 'none')};
  pointer-events: ${({ clickable }) => (clickable ? 'auto' : 'none')};
`;

const MainComponentContent = () => {
  const { selectedCard, resetCards } = useCard();
  const [animateLogo, setAnimateLogo] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState('#FFFFFF');
  const [cardsOpacity, setCardsOpacity] = useState(0);
  const [isAnimated, setIsAnimated] = useState(false);
  const [cardsClickable, setCardsClickable] = useState(false);
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const cardsRef = useRef(null);

  const location = useLocation(); // Utilisation de useLocation pour obtenir la route actuelle

  const handleEnter = () => {
    setAnimateLogo(true);
    setCardsClickable(false);

    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none';
        }

        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none';
        }

        if (cardsRef.current) {
          gsap.to(cardsRef.current, {
            opacity: 1,
            y: 0,
            duration: 0.8,
            ease: 'power4.out',
            delay: 0.1,
          });
        }

        setIsAnimated(true);
        setCardsOpacity(1);

        // Réactiver les clics après 1 seconde
        setTimeout(() => {
          setCardsClickable(true);
        }, 1000);
      },
    });
  };

  const handleXIconClick = () => {
    resetCards();
    setIsAnimated(false);
    setCardsOpacity(1);
    setCardsClickable(true);
  };

  // Masquer le logo sur les pages ActionVerite, Generator, et Roleplay
  const hideLogo = location.pathname === '/action-verite' || location.pathname === '/generator' || location.pathname === '/roleplay';

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      {!hideLogo && <Logo animate={animateLogo} />} {/* Conditionnement du rendu du logo */}
      <Navbar />

      <NavbarRight onResetAnimation={handleXIconClick} />

      {!selectedCard && (
        <CardsContainer ref={cardsRef} opacity={cardsOpacity} isAnimated={isAnimated} clickable={cardsClickable}>
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}

      <WhiteOverlay ref={overlayRef} />

      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>
    </BackgroundContainer>
  );
};

export default MainComponentContent;
