import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import lowIntensityImage from '../images/logo-5.svg';
import mediumIntensityImage from '../images/whitelogo-10.svg';
import highIntensityImage from '../images/whitelogo-20.svg';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  display: flex;
  width: 90%;
  height: 90vh;
  max-width: 1200px;
  background-color: ${({ intensity }) => 
    intensity === 'Warm-Up' ? '#FBF8F1' : 
    intensity === 'Foreplay' ? '#D51C2C' : 
    '#5A0C13'};
  background-image: ${({ intensity }) => 
    intensity === 'Warm-Up' ? `url(${lowIntensityImage})` : 
    intensity === 'Foreplay' ? `url(${mediumIntensityImage})` : 
    `url(${highIntensityImage})`};
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  border-radius: 20px;
  overflow: hidden;
  transition: background-color 0.3s ease, background-image 0.3s ease;
`;

const FormSection = styled.div`
  flex: 1;
  padding: 48px;
  overflow-y: auto;
  background-color: rgba(255, 255, 255, 1);
`;

const CardSection = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Group = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const Title = styled.h2`
  font-size: 32px;
  font-weight: bold;
  color: #000;
  margin-bottom: 32px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 24px;
`;

const ChipsContainer = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
`;

const Chip = styled.button`
  background: white;
  border: 2px solid ${props => props.selected ? 'black' : '#E2E2E2'};
  color: black;
  border-radius: 8px;
  padding: 15px 25px;
  cursor: pointer;
  font-size: 16px;
  text-align: left;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  transition: border-color 0.3s ease;
  flex: 1;

  &:hover {
    border-color: black;
  }
`;

const ChipTitle = styled.span`
  font-weight: bold;
  margin-bottom: 5px;
`;

const ChipDescription = styled.span`
  font-size: 14px;
  color: #666;
`;

const TextAreaContainer = styled.div`
  position: relative;
  width: 100%;
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 12px;
  padding-left: 80px;
  border: 1px solid #ccc;
  border-radius: 8px;
  font-size: 16px;
  min-height: 100px;
  resize: vertical;
`;

const Badge = styled.span`
  background-color: #FF69B4;
  color: white;
  padding: 2px 6px;
  border-radius: 12px;
  font-size: 14px;
  font-weight: bold;
  position: absolute;
  top: 12px;
  left: 12px;
  pointer-events: none;
`;

const Label = styled.label`
  font-size: 18px;
  font-weight: bold;
  margin-bottom: 0px;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 10px;
`;

const Button = styled.button`
  background: white;
  color: black;
  border: 2px solid ${props => props.primary ? 'black' : '#E2E2E2'};
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 16px;
  font-weight: 650;
  transition: border-color 0.3s ease;

  &:hover {
    border-color: black;
  }
`;

const Card = styled.div`
  width: 392px;
  height: 548px;
  background-color: #FBF8F1;
  border-radius: 16px;
  padding: 48px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
`;

const CardTitle = styled.h3`
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 600;
  text-transform: uppercase;
  margin-bottom: 20px;
`;

const CardContent = styled.p`
  font-family: Paragon, serif;
  font-size: 24px;
  text-align: center;
  flex-grow: 1;
  display: flex;
  align-items: center;
`;

const CardFooter = styled.div`
  font-family: 'Poppins', sans-serif;
  text-transform: uppercase;
  font-size: 14px;
  font-weight: 400;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  background: none;
  border: none;
  font-size: 24px;
  cursor: pointer;
  color: #000;
`;

const AddTruthOrDareModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    type: 'dare',
    player: 'all',
    template: '',
    intensity: 'Warm-Up',
  });

  const textareaRef = useRef(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleTypeChange = (type) => {
    setFormData({ ...formData, type });
  };

  const handlePlayerChange = (player) => {
    setFormData({ ...formData, player });
  };

  const handleIntensityChange = (intensity) => {
    setFormData({ ...formData, intensity });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:1812/api/truthordare', {
        ...formData,
        template: `{Joueur1} ${formData.template}`
      });
      alert('Truth or Dare added successfully!');
      onClose();
    } catch (error) {
      console.error('Error adding Truth or Dare:', error);
      alert('Failed to add Truth or Dare. Please try again.');
    }
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent intensity={formData.intensity}>
        <FormSection>
          <CloseButton onClick={onClose}>&times;</CloseButton>
          <Title>Ajouter un dare</Title>
          <Form onSubmit={handleSubmit}>
            <ChipsContainer>
              <Chip
                type="button"
                selected={formData.type === 'truth'}
                onClick={() => handleTypeChange('truth')}
              >
                <ChipTitle>Truth 👀</ChipTitle>
                <ChipDescription>Pour les couples débutants</ChipDescription>
              </Chip>
              <Chip
                type="button"
                selected={formData.type === 'dare'}
                onClick={() => handleTypeChange('dare')}
              >
                <ChipTitle>Dare 🔥</ChipTitle>
                <ChipDescription>Pour les couples débutants</ChipDescription>
              </Chip>
            </ChipsContainer>
            <Group>
              <Label>Gage</Label>
              <TextAreaContainer>
                <Badge>{"{Joueur1}"}</Badge>
                <TextArea
                  ref={textareaRef}
                  name="template"
                  value={formData.template}
                  onChange={handleChange}
                  placeholder="vous allez devoir vous occuper de Clémence en lui faisant un cuni."
                  required
                />
              </TextAreaContainer>
            </Group>
            <Group>
              <Label>Qui peut participer au gage ?</Label>
              <ButtonGroup>
                <Button type="button" primary={formData.player === 'all'} onClick={() => handlePlayerChange('all')}>Les deux</Button>
                <Button type="button" primary={formData.player === 'firstName2'} onClick={() => handlePlayerChange('firstName2')}>Homme</Button>
                <Button type="button" primary={formData.player === 'firstName1'} onClick={() => handlePlayerChange('firstName1')}>Femme</Button>
              </ButtonGroup>
            </Group>
            <Group>
              <Label>Intensité ?</Label>
              <ButtonGroup>
                <Button type="button" primary={formData.intensity === 'Warm-Up'} onClick={() => handleIntensityChange('Warm-Up')}>Warm-Up</Button>
                <Button type="button" primary={formData.intensity === 'Foreplay'} onClick={() => handleIntensityChange('Foreplay')}>Foreplay</Button>
                <Button type="button" primary={formData.intensity === 'MainEvent'} onClick={() => handleIntensityChange('MainEvent')}>Main Event</Button>
              </ButtonGroup>
            </Group>
            <Button type="submit" primary>Confirmer</Button>
          </Form>
        </FormSection>
        <CardSection>
          <Card>
            <CardTitle>TRUTH OR DARE</CardTitle>
            <CardContent>
              {"{Joueur1} " + formData.template}
            </CardContent>
            <CardFooter>LOVETOGETHER</CardFooter>
          </Card>
        </CardSection>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AddTruthOrDareModal;
