import React, { useState } from 'react';
import Button from './navitem';
import { FaKey, FaEye } from 'react-icons/fa'; 
import Modal from '../Modal';
import { useCard } from '../CardContext'; // Importation du contexte

const NavbarRight = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [selectedToys, setSelectedToys] = useState([]);
  const [formData, setFormData] = useState({ firstName1: '', firstName2: '' });
  const { selectedCard } = useCard(); // Accès au contexte pour vérifier si une carte est sélectionnée

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const handleSave = (data) => {
    setSelectedToys(data.selectedToys);
    setFormData({
      firstName1: data.firstName1,
      firstName2: data.firstName2,
    });
    closeModal();
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'row', position: 'absolute', right: '48px', top: '48px', gap: '10px' }}>
      <Button icon={<FaKey />} label="Filters" onClick={openModal} />
      {/* Afficher le bouton supplémentaire si une carte est sélectionnée */}
      {selectedCard && (
        <Button icon={<FaEye />} label="Retour" /> 
      )}
      <Modal 
        isOpen={isModalOpen} 
        onClose={closeModal} 
        onSave={handleSave} 
        selectedToys={selectedToys}
        formData={formData}
        setFormData={setFormData}
      />
    </div>
  );
};

export default NavbarRight;
