import React, { useState, useRef, useEffect } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import Logo from './components/Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';

const BackgroundContainer = styled.div`
  position: relative;
  background-color: #fff; /* Fond blanc */
  width: 100%;
  height: 100vh;
  overflow: hidden;
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: #fff;
  z-index: 100000002; /* Couvre le contenu */
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 10000000;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%; 
  pointer-events: all;
`;

const MainComponent = () => {
  const [loading, setLoading] = useState(true);
  const [animateLogo, setAnimateLogo] = useState(false);
  const overlayRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);  // Déclenche l'animation du logo

    // Utiliser GSAP pour animer la disparition du fond blanc
    gsap.to(overlayRef.current, {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        setLoading(false); // Supprimer le LoadingScreen après l'animation
      }
    });
  };

  return (
    <CardProvider>
      <PageProvider>
        <BackgroundContainer>
          <Logo animate={animateLogo} />
          {loading && (
            <>
              <WhiteOverlay ref={overlayRef} />
              <OverlayContainer>
                <ButtonContainer>
                  <button onClick={handleEnter}>Entrer</button>
                </ButtonContainer>
              </OverlayContainer>
            </>
          )}
          {!loading && (
            <div>
              <Navbar />
              <CardsWrapper />
            </div>
          )}
          <GrainEffect /> {/* Positionné au-dessus du fond blanc */}
        </BackgroundContainer>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
