import React from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 40px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  background: none;
  border: none;
  font-size: 24px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 32px;
  font-weight: bold;
  color: #000;
  margin-bottom: 20px;
`;

const SubTitle = styled.h3`
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 10px;
`;

const OptionGrid = styled.div`
  display: flex;
  gap: 20px;
  margin-bottom: 30px;
`;

const OptionButton = styled.button`
  background: #f9f3ef;
  border: none;
  border-radius: 16px;
  padding: 20px;
  flex: 1;
  text-align: center;
  cursor: pointer;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.05);

  &:hover {
    background: #ffe6e1;
  }
`;

const EquipmentGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const EquipmentButton = styled.button`
  background: #fff;
  border: 1px solid #ddd;
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;

  &:hover {
    background: #f0f0f0;
  }
`;

const Modal = ({ isOpen, onClose }) => {
  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Modify your experience</SectionTitle>

        <SubTitle>Select your place</SubTitle>
        <OptionGrid>
          <OptionButton>
            <span role="img" aria-label="car">🚗</span> In a car
          </OptionButton>
          <OptionButton>
            <span role="img" aria-label="beach">🏖</span> At the beach
          </OptionButton>
          <OptionButton>
            <span role="img" aria-label="bedroom">🛏</span> The Bedroom
          </OptionButton>
        </OptionGrid>

        <SubTitle>Select your equipment</SubTitle>
        <EquipmentGrid>
          <EquipmentButton>Lubrifiant</EquipmentButton>
          <EquipmentButton>Shibari</EquipmentButton>
          <EquipmentButton>Massage oil</EquipmentButton>
          <EquipmentButton>Butt plugs</EquipmentButton>
          <EquipmentButton>Penis God</EquipmentButton>
          <EquipmentButton>Lush 3</EquipmentButton>
          <EquipmentButton>BlindFold</EquipmentButton>
          <EquipmentButton>Neckchain</EquipmentButton>
          <EquipmentButton>HandCuffs</EquipmentButton>
          <EquipmentButton>Vibro</EquipmentButton>
          <EquipmentButton>Cockring</EquipmentButton>
          <EquipmentButton>Suction stimulator</EquipmentButton>
        </EquipmentGrid>
      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
