import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const OverlayContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  pointer-events: none;
  z-index: 2;
`;

const OverlayShape = styled.div`
  position: absolute;
  background-color: #7FE3E1; /* Couleur de la forme */
  width: 300%; /* Largeur initiale pour permettre l'effet de courbe */
  height: 300%;
  top: -100%;
  left: -100%;
  border-radius: 50%; /* Pour créer l'effet de courbe */
`;

const Overlay = ({ triggerAnimation }) => {
  const shapeRef = useRef();

  useEffect(() => {
    if (triggerAnimation) {
      gsap.to(shapeRef.current, {
        duration: 2, // Durée de l'animation
        x: '100%', // Déplace la forme vers la droite
        y: '100%',
        ease: 'power2.inOut',
        onComplete: () => {
          // Action après la fin de l'animation (comme rediriger vers une autre page)
        },
      });
    }
  }, [triggerAnimation]);

  return (
    <OverlayContainer>
      <OverlayShape ref={shapeRef} />
    </OverlayContainer>
  );
};

export default Overlay;
