import React, { useState } from 'react';
import styled from 'styled-components';
import HomeCard from './HomeCard';
import TransitionPage from './NavbarTransitionPage';

const Wrapper = styled.div`
  display: flex;
  width: '100vw',
  height: '100vh',
  z-index: 1;
  justify-content: center;
  align-items: center;
  gap: 20px;
  padding: 50px;
`;

const CardsWrapper = ({ setBackgroundColor }) => {
  const [selectedCard, setSelectedCard] = useState(null);

  const handleCardClick = (card) => {
    setSelectedCard(card);
  };

  const handleBack = () => {
    setSelectedCard(null);
  };

  return (
    <>
      {!selectedCard && (
        <Wrapper>
          <HomeCard
            image={require('../images/junebaby.png')}
            rotate="-5deg"
            onMouseEnter={() => setBackgroundColor('#FFD700')}
            onMouseLeave={() => setBackgroundColor('#FBF8F1')}
            onClick={() => {
              handleCardClick('card1');
              setTimeout(() => {
                setBackgroundColor('#FBF8F1');
              }, 1000); // 1000ms = 1 seconde
            }}
          />
          <HomeCard
            image={require('../images/yourself.png')}
            rotate="0deg"
            onMouseEnter={() => setBackgroundColor('#FF69B4')}
            onMouseLeave={() => setBackgroundColor('#FBF8F1')}
            onClick={() => {
              handleCardClick('card2');
              setTimeout(() => {
                setBackgroundColor('#FBF8F1');
              }, 1000); // 1000ms = 1 seconde
            }}
          />
          <HomeCard
            image={require('../images/love.png')}
            rotate="5deg"
            onMouseEnter={() => setBackgroundColor('#FF4500')}
            onMouseLeave={() => setBackgroundColor('#FBF8F1')}
            onClick={() => {
              handleCardClick('card3');
              setTimeout(() => {
                setBackgroundColor('#FBF8F1');
              }, 1000); // 1000ms = 1 seconde
            }}
          />
        </Wrapper>
      )}

      {selectedCard && (
        <TransitionPage
          selectedCard={selectedCard}
          onBack={handleBack} // Pour revenir en arrière si nécessaire
        />
      )}
    </>
  );
};

export default CardsWrapper;
