import React, { useState } from 'react';
import LineAnimation from './LineAnimation';

const App = () => {
  const [trigger, setTrigger] = useState(false);

  const handleCardClick = () => {
    setTrigger(true);
  };

  return (
    <div>
      <button onClick={handleCardClick}>Trigger Animation</button>
      {trigger && <LineAnimation triggerAnimation={trigger} />}
    </div>
  );
};

export default App;
