import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const OverlayWrapper = styled.div`
  position: absolute;
  top: 50%;
  left: 0;
  width: 100%;
  height: 10px; /* Épaisseur du trait */
  background-color: #FF4500; /* Couleur du trait */
  clip-path: inset(0 100% 0 0); /* Masque initial qui cache le trait */
  z-index: 2; /* Assurez-vous que le trait est au-dessus de tout */
`;

const Overlay = ({ triggerAnimation }) => {
  const overlayRef = useRef();

  useEffect(() => {
    if (triggerAnimation) {
      gsap.to(overlayRef.current, {
        duration: 10, // Durée de l'animation
        clipPath: 'inset(0 0 0 0)', // Révèle le trait en animant le clip-path
        ease: 'linear' // Linear pour une animation constante
      });
    }
  }, [triggerAnimation]);

  return <OverlayWrapper ref={overlayRef} />;
};

export default Overlay;
