import React, { useRef } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';
import Logo from './Logo';
import GrainEffect from './GrainEffect';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
  z-index: 10;
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  z-index: 1;
  pointer-events: none;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: ;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto;
`;

const EntranceAnimation = ({ onEnterComplete, setAnimateLogo }) => {
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none';
        }

        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none';
        }

        onEnterComplete(); // Appel pour indiquer que l'animation est terminée
      },
    });
  };

  return (
    <BackgroundContainer>
      <Logo animate={true} />
      <WhiteOverlay ref={overlayRef} />
      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>
      <GrainEffect />
    </BackgroundContainer>
  );
};

export default EntranceAnimation;
