import React from 'react';
import styled from 'styled-components';

const AVModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const AVModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const AVModalButton = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: black;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: white;
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  margin: 8px;
`;

const AVModal = ({ showModal, handleModalAccept, handleModalDecline }) => {
  if (!showModal) return null;

  return (
    <AVModalOverlay>
      <AVModalContent>
        <h2>Passer à Foreplay ?</h2>
        <p>Vous avez cliqué 10 fois sur une carte Dare en Warm-Up. Voulez-vous passer à Foreplay ?</p>
        <div>
          <AVModalButton onClick={handleModalAccept}>Oui</AVModalButton>
          <AVModalButton onClick={handleModalDecline}>Non</AVModalButton>
        </div>
      </AVModalContent>
    </AVModalOverlay>
  );
};

export default AVModal;
