import React, { useRef } from 'react';
import styled from 'styled-components';
import { gsap } from 'gsap';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #FBF8F1;
`;

const LogoContainer = styled.div`
  position: relative;
  width: 200px;
  height: 200px;
  background-image: url('https://via.placeholder.com/200'); /* Remplacez par l'URL de votre logo */
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
`;

const Fill = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #ff4500;
  transform-origin: bottom;
  transform: scaleY(0);
`;

const Button = styled.button`
  margin-top: 20px;
  padding: 10px 20px;
  background-color: #ff4500;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #ff5733;
  }
`;

const SimpleFillExample = () => {
  const fillRef = useRef(null);

  const startFilling = () => {
    if (fillRef.current) {
      console.log('Starting the fill animation'); // Log pour vérifier le démarrage de l'animation
      gsap.to(fillRef.current, { scaleY: 1, duration: 5, ease: 'linear' }); // Remplissage en 5 secondes
    } else {
      console.log('fillRef is null'); // Log pour vérifier si fillRef est correctement référencé
    }
  };

  return (
    <Container>
      <LogoContainer>
        <Fill ref={fillRef} />
      </LogoContainer>
      <Button onClick={startFilling}>Start Filling</Button>
    </Container>
  );
};

export default SimpleFillExample;
