import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';

const CardsBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #faf3d9;
  z-index: 100000000;
`;

const Content = styled.div`
  position: relative;
  z-index: 10;
  text-align: center;
  max-width: 800px;
`;

const Title = styled.h1`
  font-size: 4rem;
  margin: 0;
  color: #191919;
  opacity: 0; /* Start invisible */
  animation: fadeIn 2s forwards; /* Fade in animation */
`;

const Subtitle = styled.h2`
  font-size: 3rem;
  margin-top: 0.5rem;
  color: #191919;
  opacity: 0; /* Start invisible */
  animation: fadeIn 3s forwards; /* Fade in animation with delay */
`;

const fadeInKeyframes = `
  @keyframes fadeIn {
    to {
      opacity: 1;
    }
  }
`;

function App() {
  const titleRef = useRef(null);
  const subtitleRef = useRef(null);

  useEffect(() => {
    // Animation logic can be added here if needed
  }, []);

  return (
    <>
      <style>{fadeInKeyframes}</style>
      <CardsBackground>
        <Content>
          <Title ref={titleRef}>A NIGHT OF CONVERSATION</Title>
          <Subtitle ref={subtitleRef}>EXPLORING THE CONFLICTS IN DESIGN</Subtitle>
        </Content>
      </CardsBackground>
    </>
  );
}

export default App;
