import React, { useRef, useState, useEffect } from 'react';
import styled from 'styled-components';
import Navbar from './navbar/navbar';
import CardsWrapper from './CardsWrapper';
import { useCard } from './CardContext';
import NavbarRight from './navbar/navbarright';
import CardRenderer from './CardRenderer';
import gsap from 'gsap';
import Logo from './Logo';
import GrainEffect from './GrainEffect';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const LogoContainer = styled.div`
  position: absolute;
  top: 50px;
  left: 50%;
  transform: translateX(-50%) scale(0.2);
  transform-origin: center top;
  z-index: 100000;
`;

const CardsContainer = styled.div`
  opacity: 1; /* On fixe l'opacité à 1 par défaut */
  transform: translateY(${({ isAnimated }) => (isAnimated ? '0' : '50px')});
  transition: transform 0.8s ease, opacity 0.8s ease;
  pointer-events: ${({ clickable }) => (clickable ? 'auto' : 'none')};
  z-index: 20;
`;

const GrainContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
`;

const MainContent = ({ backgroundColor, setBackgroundColor }) => {
  const { selectedCard, resetCards } = useCard();
  const [isAnimated, setIsAnimated] = useState(false);
  const [cardsClickable, setCardsClickable] = useState(false);
  const cardsRef = useRef(null);

  const handleXIconClick = () => {
    resetCards();
    setIsAnimated(false);

    // Forcer le fond à se mettre en blanc
    setBackgroundColor('#ffffff');

    // Utiliser gsap pour forcer l'opacité à 1 instantanément
    if (cardsRef.current) {
      gsap.to(cardsRef.current, {
        opacity: 1, // Forcer l'opacité à 1
        duration: 0.2, // Animation rapide pour rendre les cartes visibles
        ease: 'power1.out',
        onComplete: () => {
          setCardsClickable(true);
        },
      });
    }
  };

  // Utiliser useEffect pour appliquer la couleur de fond immédiatement
  useEffect(() => {
    if (backgroundColor === '#ffffff') {
      gsap.to(document.body, {
        backgroundColor: '#ffffff',
        duration: 0.1, // Rapide changement de couleur
      });
    }
  }, [backgroundColor]);

  React.useEffect(() => {
    // Démarrer l'animation des cartes lors du montage du composant
    if (cardsRef.current) {
      gsap.to(cardsRef.current, {
        opacity: 1,
        y: 0,
        duration: 0.8,
        ease: 'power4.out',
        delay: 0.1,
        onComplete: () => {
          setCardsClickable(true);
        },
      });
    }
  }, []);

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <GrainContainer>
        <GrainEffect />
      </GrainContainer>

      <LogoContainer>
        <Logo />
      </LogoContainer>

      <Navbar />
      <NavbarRight onResetAnimation={handleXIconClick} />

      {!selectedCard && (
        <CardsContainer ref={cardsRef} isAnimated={isAnimated} clickable={cardsClickable}>
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}
    </BackgroundContainer>
  );
};

export default MainContent;
