import React, { useState } from 'react';
import styled from 'styled-components';
import HomeCard from './HomeCard';
import Overlay from './Overlay';

const Wrapper = styled.div`
  display: flex;
  height: 100vh;
  z-index: 1;
  justify-content: center;
  align-items: center;
  gap: 20px;
  padding: 50px;
  position: relative;
`;

const CardsWrapper = ({ setBackgroundColor }) => {
  const [triggerAnimation, setTriggerAnimation] = useState(false);

  const handleCardClick = () => {
    setTriggerAnimation(true);
    setTimeout(() => {
      // Ici, vous pouvez naviguer vers une nouvelle page ou section
    }, 1500); // La durée doit correspondre à l'animation GSAP
  };

  return (
    <Wrapper>
      <Overlay triggerAnimation={triggerAnimation} />
      <HomeCard
        image={require('../images/junebaby.png')}
        rotate="-5deg"
        onMouseEnter={() => setBackgroundColor('#FFD700')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
        onClick={handleCardClick}
      />
      <HomeCard
        image={require('../images/yourself.png')}
        rotate="0deg"
        onMouseEnter={() => setBackgroundColor('#FF69B4')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
        onClick={handleCardClick}
      />
      <HomeCard
        image={require('../images/love.png')}
        rotate="5deg"
        onMouseEnter={() => setBackgroundColor('#FF4500')}
        onMouseLeave={() => setBackgroundColor('#FBF8F1')}
        onClick={handleCardClick}
      />
    </Wrapper>
  );
};

export default CardsWrapper;
