import React, { useState } from 'react';
import styled from 'styled-components';
import HomeCard from './HomeCard';
import LottieAnimation from './LottieAnimation';
import ActionVerite from './ActionVerite';
import Roleplay from './ActionVerite';

const Wrapper = styled.div`
  display: flex;
  height: 100vh;
  z-index: 1;
  justify-content: center;
  align-items: center;
  gap: 20px;
  padding: 50px;
`;

const NewPageContent = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  font-size: 2em;
  color: #333;
  flex-direction: column;
`;

const CardsWrapper = ({ setBackgroundColor }) => {
  const [isLottieVisible, setLottieVisible] = useState(false);
  const [isNewPageVisible, setNewPageVisible] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);

  const handleCardClick = (card) => {
    setSelectedCard(card);
    setLottieVisible(true);
  };

  const handleAnimationComplete = () => {
    setLottieVisible(false);
    setNewPageVisible(true); // Affiche la nouvelle page après l'animation
  };

  const getLottieData = () => {
    switch (selectedCard) {
      case 'card1':
        return require('../test.json'); // Remplacez par le chemin correct
      case 'card2':
        return require('../test.json'); // Remplacez par le chemin correct
      case 'card3':
        return require('../test.json'); // Remplacez par le chemin correct
      default:
        return null;
    }
  };

  const getNewContent = () => {
    switch (selectedCard) {
      case 'card1':
        return <ActionVerite />; // Affiche le composant ActionVerite au clic sur la première carte
      case 'card2':
        return <div>New content for Card 2</div>;
      case 'card3':
        return <Roleplay />; // Affiche le composant ActionVerite au clic sur la première carte
      default:
        return null;
    }
  };

  return (
    <>
      {!isLottieVisible && !isNewPageVisible && (
        <Wrapper>
          <HomeCard
            image={require('../images/junebaby.png')}
            rotate="-5deg"
            onMouseEnter={() => setBackgroundColor('#FFD700')}
            onMouseLeave={() => setBackgroundColor('#FBF8F1')}
            onClick={() => {
              handleCardClick('card1');
              setTimeout(() => {
                  setBackgroundColor('#FBF8F1');
              }, 1000); // 1000ms = 1 seconde
          }}

          />
          <HomeCard
            image={require('../images/yourself.png')}
            rotate="0deg"
            onMouseEnter={() => setBackgroundColor('#FF69B4')}
            onMouseLeave={() => setBackgroundColor('#FBF8F1')}
            onClick={() => {
              handleCardClick('card2');
              setTimeout(() => {
                  setBackgroundColor('#FBF8F1');
              }, 1000); // 1000ms = 1 seconde
          }}
          />
          <HomeCard
            image={require('../images/love.png')}
            rotate="5deg"
            onMouseEnter={() => setBackgroundColor('#FF4500')}
            onMouseLeave={() => setBackgroundColor('#FBF8F1')}
            onClick={() => {
              handleCardClick('card3');
              setTimeout(() => {
                  setBackgroundColor('#FBF8F1');
              }, 1000); // 1000ms = 1 seconde
          }}
          />
        </Wrapper>
      )}

      {isLottieVisible && (
        <LottieAnimation
          animationData={getLottieData()}
          onComplete={handleAnimationComplete}
        />
      )}

      {!isLottieVisible && isNewPageVisible && (
        <NewPageContent>
          {getNewContent()}
        </NewPageContent>
      )}
    </>
  );
};

export default CardsWrapper;
