import React, { useState, useRef } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import Logo from './components/Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';

const BackgroundContainer = styled.div`
  position: relative;
  background-color: #fff;
  width: 100%;
  height: 100vh;
  overflow: hidden;
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: #fff;
  z-index: 100000002;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: all;
`;

const GrainOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  pointer-events: none;
  z-index: 100000004;
`;

const LogoWrapper = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 100000005;
  pointer-events: none;
`;

const MainComponent = () => {
  const [animateLogo, setAnimateLogo] = useState(false);
  const [showGrain, setShowGrain] = useState(true); // État pour contrôler la visibilité du grain
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    // Utiliser GSAP pour animer la disparition du fond blanc et du bouton
    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        overlayRef.current.style.display = 'none'; // Cache complètement le fond blanc après l'animation
        setShowGrain(false); // Retire le grain au clic sur "Entrer"
      }
    });
  };

  return (
    <CardProvider>
      <PageProvider>
        <BackgroundContainer>
          <LogoWrapper>
            <Logo animate={animateLogo} />
          </LogoWrapper>
          <Navbar />
          <CardsWrapper />

          {/* Premier effet de grain, contrôlé par l'état showGrain */}
          {showGrain && <GrainEffect />} 

          {/* Couche de superposition blanche qui sera animée pour disparaître */}
          <WhiteOverlay ref={overlayRef} />

          {/* Overlay pour le bouton */}
          <OverlayContainer>
            <ButtonContainer ref={buttonRef}>
              <button onClick={handleEnter}>Entrer</button>
            </ButtonContainer>
          </OverlayContainer>

          {/* Grain effect au-dessus du fond blanc */}
          <GrainOverlay>
            <GrainEffect />
          </GrainOverlay>

        </BackgroundContainer>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
