import React, { useRef, useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import gsap from 'gsap';
import lowIntensityImage from '../images/logo-5.svg';
import mediumIntensityImage from '../images/whitelogo-10.svg';
import highIntensityImage from '../images/whitelogo-20.svg';
import GrainEffect from './GrainEffect';
import Modal from './Modal'; // Import your Modal component

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100vw;
  overflow: hidden;
  background-color: ${({ intensity }) => 
    intensity === 'low' ? '#FBF8F1' : 
    intensity === 'medium' ? '#D51C2C' : 
    '#5A0C13'};
  background-image: ${({ intensity }) => 
    intensity === 'low' ? `url(${lowIntensityImage})` : 
    intensity === 'medium' ? `url(${mediumIntensityImage})` : 
    `url(${highIntensityImage})`};
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  position: relative;
  z-index: 1;
`;

const CardsContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 5;
`;

const CardWrapper = styled.div`
  perspective: 1000px;
  width: 392px;
  height: 548px;
  position: absolute;
  cursor: pointer;
  z-index: 5;
`;

const Footer = styled.div`
  font-family: 'Poppins', sans-serif;
  text-transform: uppercase;
  font-size: 14px;
  font-weight: 400;
  color: #000000;
  margin-top: 20px;
`;

const CardInner = styled.div`
  width: 100%;
  height: 100%;
  transition: transform 1s;
  transform-style: preserve-3d;
  position: relative;
`;

const CardFace = styled.div`
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center;
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  position: absolute;
  backface-visibility: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  box-sizing: border-box;
`;

const CardFront = styled(CardFace)`
  background-image: url(${props => props.image});
`;

const Title = styled.h1`
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 600;
  margin-bottom: 20px;
`;

const CardBack = styled(CardFace)`
  background-color: white;
  transform: rotateY(180deg);
  display: flex;
  flex-direction: column;
  padding: 48px;
  justify-content: space-between;
`;

const CardText = styled.div`
  color: #333;
  font-family: Paragon;
  font-size: 1.75rem;
  text-align: center;
`;

const ButtonContainer = styled.div`
  position: absolute;
  bottom: 48px;
  display: flex;
  gap: 20px;
  margin-top: 20px;
  z-index: 5;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover:enabled {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.6;
  }
`;

const TimerButton = styled(Button)`
  background-color: #ff4500;
  color: white;

  &:hover {
    background-color: #ff5733;
  }
`;

const ActionVerite = () => {
  const [clickedCard, setClickedCard] = useState(null);
  const [randomText, setRandomText] = useState('');
  const [duration, setDuration] = useState(null);
  const [remainingTime, setRemainingTime] = useState(null);
  const [isPaused, setIsPaused] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const intervalRef = useRef(null);

  const leftCardRef = useRef(null);
  const rightCardRef = useRef(null);

  const [selectedToys, setSelectedToys] = useState([]);
  const [currentPlayer, setCurrentPlayer] = useState('');
  const [firstName1, setFirstName1] = useState('');
  const [firstName2, setFirstName2] = useState('');
  const [intensity, setIntensity] = useState('low');
  const [isModalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    loadUserPreferences();
  }, []);

  const loadUserPreferences = () => {
    const savedPreferences = localStorage.getItem('userPreferences');
    if (savedPreferences) {
      const { firstName1, firstName2, selectedToys } = JSON.parse(savedPreferences);
      setFirstName1(firstName1 || '');
      setFirstName2(firstName2 || '');
      setSelectedToys(selectedToys || []);
      setCurrentPlayer(firstName1 || '');
    }
  };

  const handleSave = (data) => {
    setFirstName1(data.firstName1);
    setFirstName2(data.firstName2);
    setSelectedToys(data.selectedToys);
    setCurrentPlayer(data.firstName1); 
    console.log("Data saved:", data);
  };

  const updateUserData = (data) => {
    console.log("updateUserData called with:", data);
    loadUserPreferences(); // Recharge les préférences de l'utilisateur depuis le localStorage
  };

  const fetchRandomActionOrTruth = async (type, player) => {
    try {
      const mappedPlayer = player === firstName1 ? 'firstName1' : 'firstName2';
      const toysParam = selectedToys?.length ? [...selectedToys, 'all'] : ['all'];

      const response = await axios.get(`http://localhost:1812/api/truthordare/random`, {
        params: { type, player: mappedPlayer, toys: toysParam.join(','), intensity }
      });

      if (response.data && response.data.template) {
        const { template, duration } = response.data;
        setDuration(duration || null);
        setRemainingTime(duration);
        return template;
      } else {
        return 'No data available';
      }
    } catch (error) {
      console.error('Error loading text', error);
      return 'Error loading text';
    }
  };

  const handleCardClick = async (card) => {
    if (clickedCard) return;

    try {
      let randomText = '';

      if (card === 'left') {
        randomText = await fetchRandomActionOrTruth('truth', currentPlayer);
      } else {
        randomText = await fetchRandomActionOrTruth('dare', currentPlayer);
      }

      setRandomText(randomText);
      setClickedCard(card);

      if (card === 'left') {
        gsap.to(leftCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
        gsap.to(rightCardRef.current, { duration: 0.8, x: 1000, opacity: 0 });
        setTimeout(() => {
          gsap.to(leftCardRef.current.querySelector('.inner'), { rotationY: 180 });
        }, 500);
      } else {
        gsap.to(rightCardRef.current, { duration: 0.8, x: 0, y: 0, rotation: 0, scale: 1 });
        gsap.to(leftCardRef.current, { duration: 0.8, x: -1000, opacity: 0 });
        setTimeout(() => {
          gsap.to(rightCardRef.current.querySelector('.inner'), { rotationY: 180 });
        }, 500);
      }
    } catch (error) {
      console.error('Error handling card click:', error);
    }
  };

  const resetCards = () => {
    setClickedCard(null);
    setRandomText('');
    setDuration(null);
    setRemainingTime(null);
    setIsPaused(false);
    setIsStarted(false);
    clearInterval(intervalRef.current);
    setCurrentPlayer(prevPlayer => prevPlayer === firstName1 ? firstName2 : firstName1);
    gsap.to(leftCardRef.current.querySelector('.inner'), { rotationY: 0 });
    gsap.to(rightCardRef.current.querySelector('.inner'), { rotationY: 0 });
    gsap.to(leftCardRef.current, { duration: 0.8, x: '-150px', rotation: -10, scale: 1, opacity: 1 });
    gsap.to(rightCardRef.current, { duration: 0.8, x: '150px', rotation: 10, scale: 1, opacity: 1 });
  };

  const startTimer = () => {
    if (!remainingTime) return;

    setIsStarted(true);
    setIsPaused(false);

    intervalRef.current = setInterval(() => {
      setRemainingTime(prevTime => {
        if (prevTime <= 1) {
          clearInterval(intervalRef.current);
          setIsPaused(true);
          setIsStarted(false);
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);
  };

  const pauseTimer = () => {
    setIsPaused(true);
    clearInterval(intervalRef.current);
  };

  const resetTimer = () => {
    clearInterval(intervalRef.current);
    setRemainingTime(duration);
    setIsPaused(false);
    startTimer();
  };

  const handleTimerButtonClick = () => {
    if (remainingTime === 0) {
      resetTimer();
    } else if (!isStarted) {
      startTimer();
    } else if (isPaused) {
      startTimer();
    } else {
      pauseTimer();
    }
  };

  const changeIntensity = () => {
    setIntensity(prev => {
      if (prev === 'low') return 'medium';
      if (prev === 'medium') return 'high';
      return 'low';
    });
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
  };

  return (
    <Container intensity={intensity}>
      <CardsContainer>
        <CardWrapper
          ref={leftCardRef}
          onClick={() => handleCardClick('left')}
        >
          <CardInner className="inner">
            <CardFront image={require('../images/junebaby.png')} />
            <CardBack>
              <Title>Truth & Dare</Title>
              <CardText>{clickedCard === 'left' && randomText ? `${currentPlayer}, ${randomText}` : currentPlayer}</CardText>
              <Footer>LoveTogether</Footer>
            </CardBack>
          </CardInner>
        </CardWrapper>
        <CardWrapper
          ref={rightCardRef}
          onClick={() => handleCardClick('right')}
        >
          <CardInner className="inner">
            <CardFront image={require('../images/love.png')} />
            <CardBack>
              <Title>Truth & Dare</Title>
              <CardText>{clickedCard === 'right' && randomText ? `${currentPlayer}, ${randomText}` : currentPlayer}</CardText>
              <Footer>LoveTogether</Footer>
            </CardBack>
          </CardInner>
        </CardWrapper>
      </CardsContainer>
      <ButtonContainer>
        {clickedCard ? (
          <>
            <Button onClick={resetCards}>
              <span role="img" aria-label="Tour">👤</span> Joueur suivant
            </Button>
            {duration && (
              <TimerButton onClick={handleTimerButtonClick}>
                {remainingTime === 0 ? 'Recommencer' : isPaused ? 'Reprendre' : `Pause (${formatTime(remainingTime)})`}
              </TimerButton>
            )}
          </>
        ) : (
          <Button disabled>
            <span role="img" aria-label="Tour">👤</span> À ton tour, {currentPlayer}
          </Button>
        )}
        {!clickedCard && (
          <Button onClick={changeIntensity}>
            {intensity === 'low' ? '👀 Découverte' : intensity === 'medium' ? '🔥 Préliminaires' : '🔞 Dans l\'action'}
          </Button>
        )}
      </ButtonContainer>
      <GrainEffect />
      <Modal
        isOpen={isModalOpen}e
        onClose={() => {
          setModalOpen(false);
          loadUserPreferences(); // Recharge les données quand le modal est fermé
        }}
        onSave={handleSave}
        onUserDataUpdate={updateUserData}
        selectedToys={selectedToys}
        formData={{ firstName1, firstName2 }}
        setFormData={({ firstName1, firstName2 }) => {
          setFirstName1(firstName1);
          setFirstName2(firstName2);
        }}
      />
    </Container>
  );
};

export default ActionVerite;
