import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const CardsBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #faf3d9;
  z-index: 100000000;
`;

const Content = styled.div`
  position: relative;
  z-index: 10;
  text-align: center;
  max-width: 800px;
`;

const Title = styled.h1`
  font-size: 4rem;
  margin: 0;
  color: #191919;
  overflow: hidden;
`;

const Subtitle = styled.h2`
  font-size: 3rem;
  margin-top: 0.5rem;
  color: #191919;
  overflow: hidden;
`;

const Line = styled.span`
  display: block;
  opacity: 0;
  transform: translateY(50px);
  animation: fadeInUp 0.8s forwards;
  animation-delay: ${props => props.delay}s;
  white-space: pre;
`;

const fadeInUpKeyframes = `
  @keyframes fadeInUp {
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

const App = () => {
  const [linesTitle, setLinesTitle] = useState([]);
  const [linesSubtitle, setLinesSubtitle] = useState([]);

  useEffect(() => {
    const splitTextIntoLines = (text) => {
      return text.split('\n').map((line, index) => (
        <Line key={index} delay={index * 0.2}>
          {line}
        </Line>
      ));
    };

    setLinesTitle(splitTextIntoLines('A NIGHT OF CONVERSATION'));
    setLinesSubtitle(splitTextIntoLines('EXPLORING THE CONFLICTS IN DESIGN'));
  }, []);

  return (
    <>
      <style>{fadeInUpKeyframes}</style>
      <CardsBackground>
        <Content>
          <Title>{linesTitle}</Title>
          <Subtitle>{linesSubtitle}</Subtitle>
        </Content>
      </CardsBackground>
    </>
  );
};

export default App;
