import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import lowIntensityImage from '../images/logo-5.svg';
import mediumIntensityImage from '../images/whitelogo-10.svg';
import highIntensityImage from '../images/whitelogo-20.svg';

// ... (autres imports et styled components restent inchangés)

const ModalContent = styled.div`
  display: flex;
  width: 90%;
  height: 90vh;
  max-width: 1200px;
  background: ${({ intensities }) => getBackgroundStyle(intensities)};
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  border-radius: 20px;
  overflow: hidden;
  transition: background 0.3s ease;
`;

// ... (autres styled components restent inchangés)

const getBackgroundStyle = (intensities) => {
  const intensityColors = {
    'Warm-Up': '#FBF8F1',
    'Foreplay': '#D51C2C',
    'MainEvent': '#5A0C13'
  };

  const intensityImages = {
    'Warm-Up': lowIntensityImage,
    'Foreplay': mediumIntensityImage,
    'MainEvent': highIntensityImage
  };

  if (intensities.length === 0) return '#FBF8F1';

  if (intensities.length === 1) {
    return `
      background-color: ${intensityColors[intensities[0]]};
      background-image: url(${intensityImages[intensities[0]]});
    `;
  }

  const gradientStops = intensities.map((intensity, index) => {
    const percentage = (index / (intensities.length - 1)) * 100;
    return `${intensityColors[intensity]} ${percentage}%`;
  });

  const imageLayersStyle = intensities.map((intensity, index) => {
    const percentage = (index / intensities.length) * 100;
    return `url(${intensityImages[intensity]}) ${percentage}% ${percentage + (100 / intensities.length)}%`;
  }).join(', ');

  return `
    background: linear-gradient(to right, ${gradientStops.join(', ')});
    background-image: ${imageLayersStyle};
  `;
};

const AddTruthOrDareModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    type: 'dare',
    player: 'all',
    template: '',
    intensities: [],
    toys: [],
  });

  // ... (autres états et useEffect restent inchangés)

  const handleIntensityChange = (intensity) => {
    setFormData(prevState => {
      const updatedIntensities = prevState.intensities.includes(intensity)
        ? prevState.intensities.filter(i => i !== intensity)
        : [...prevState.intensities, intensity];
      return { ...prevState, intensities: updatedIntensities };
    });
  };

  // ... (autres fonctions de gestion restent inchangées)

  return (
    <ModalOverlay>
      <ModalContent intensities={formData.intensities}>
        <FormSection>
          {/* ... (autres éléments du formulaire restent inchangés) */}
          <Group>
            <Label>Intensité ?</Label>
            <ButtonGroup>
              <Button
                type="button"
                primary={formData.intensities.includes('Warm-Up')}
                onClick={() => handleIntensityChange('Warm-Up')}
              >
                Warm-Up
              </Button>
              <Button
                type="button"
                primary={formData.intensities.includes('Foreplay')}
                onClick={() => handleIntensityChange('Foreplay')}
              >
                Foreplay
              </Button>
              <Button
                type="button"
                primary={formData.intensities.includes('MainEvent')}
                onClick={() => handleIntensityChange('MainEvent')}
              >
                Main Event
              </Button>
            </ButtonGroup>
          </Group>
          {/* ... (autres éléments du formulaire restent inchangés) */}
        </FormSection>
        <CardSection>
          {/* ... (contenu de la carte reste inchangé) */}
        </CardSection>
      </ModalContent>
    </ModalOverlay>
  );
};

export default AddTruthOrDareModal;
