import React, { useState } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider, useCard } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import LoadingScreen from './components/LoadingScreen';
import Logo from './components/Logo'; // Import du composant Logo
import styled from 'styled-components';

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000002; /* Conteneur pour le bouton, au-dessus du logo */
  pointer-events: none; /* Pour que ce conteneur ne capte pas les clics */
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%; 
  pointer-events: all; /* Rend le bouton cliquable */
`;

const MainComponent = () => {
  const [loading, setLoading] = useState(true);
  const [logoPosition, setLogoPosition] = useState({
    size: '300px',
    top: '50%',
    left: '50%',
  });

  const handleEnter = () => {
    // Définir la nouvelle position et taille du logo après la disparition de LoadingScreen
    setLogoPosition({
      size: '100px',
      top: '20px',
      left: '20px',
    });

    // Faire disparaître le LoadingScreen après le clic
    setTimeout(() => {
      setLoading(false);
    }, 1000); // Donne le temps à l'animation du logo de se terminer
  };

  return (
    <CardProvider>
      <PageProvider>
        <div>
          {/* Le logo est au-dessus du LoadingScreen */}
          <Logo size={logoPosition.size} top={logoPosition.top} left={logoPosition.left} />
          {loading && (
            <OverlayContainer>
              <ButtonContainer>
                <button onClick={handleEnter}>Entrer</button>
              </ButtonContainer>
            </OverlayContainer>
          )}
          {!loading && (
            <div>
              <Navbar />
              <CardsWrapper />
              <GrainEffect />
            </div>
          )}
        </div>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
