import React from 'react';
import styled from 'styled-components';


const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: -5px; /* Espace entre les cartes */
  padding: 50px;
  transition: background-color 0.3s ease;

  /* Vérification des propriétés : s'assurer que les éléments enfants sont bien visibles */
  & > div {
    flex-shrink: 0; /* Empêche les cartes de se réduire si elles sont trop grandes */
  }
`;



const HomeCard = styled.div`
  width: 220px;
  height: 300px;
  background-color: ${props => props.backgroundColor || 'white'};
  border-radius: 16px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  transform: rotate(${props => props.rotate || '0deg'});
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: rotate(${props => props.rotate || '0deg'}) translateY(-10px);
    box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.3);
  }
`;

const CardsWrapper = () => {
  return (
    <Wrapper>
      <HomeCard backgroundColor="red" rotate="-5deg">Card 1</HomeCard>
      <HomeCard backgroundColor="blue" rotate="0deg">Card 2</HomeCard>
      <HomeCard backgroundColor="green" rotate="5deg">Card 3</HomeCard>
    </Wrapper>
  );
};


export default CardsWrapper;
