import React, { useState, useContext } from 'react';
import Modal from '../Modal';
import { useCard } from '../CardContext'; // Importation du contexte pour les cartes
import { UserContext } from '../UserContext'; // Importation du contexte pour User
import { FaUser, FaArrowLeft } from 'react-icons/fa'; // Importation des icônes nécessaires
import { ReactComponent as AwardIcon } from '../../images/assets/icons/x-square.svg';
import NavButton from './navbarbutton'; // Importation du nouveau bouton

const NavbarRight = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const { selectedCard, resetCard } = useCard(); // Accès au contexte pour réinitialiser l'état des cartes
  const { updateUserPreferences } = useContext(UserContext); // Accès au contexte User

  const openModal = () => {
    console.log("NavbarRight - Opening modal...");
    setModalOpen(true);
  };

  const closeModal = () => {
    console.log("NavbarRight - Closing modal...");
    setModalOpen(false);
  };

  const handleSave = (data) => {
    console.log("NavbarRight - Data saved from modal:", data);
    updateUserPreferences(data); // Mettre à jour le contexte utilisateur
    closeModal();
  };

  const handleReturn = () => {
    console.log("NavbarRight - Returning to initial state. Hiding the card...");
    resetCard(); // Réinitialiser l'état des cartes pour cacher la carte sélectionnée
    console.log("NavbarRight - Card has been reset.");
  };

  console.log("NavbarRight - Current selectedCard:", selectedCard);

  return (
    <div style={{ display: 'flex', flexDirection: 'row', position: 'absolute', right: '48px', top: '48px', gap: '10px' }}>
      <NavButton icon={<FaUser />} label="Filtres" onClick={openModal} />
      {selectedCard && (
        <NavButton icon={<FaArrowLeft />} onClick={handleReturn} />
      )}
      <Modal 
        isOpen={isModalOpen} 
        onClose={closeModal} 
        onSave={handleSave} 
      />
    </div>
  );
};

export default NavbarRight;
