import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import LogoImage from '../images/logo-5.svg'; // Assurez-vous que le chemin est correct
import { gsap } from 'gsap';

const LogoContainer = styled.img`
  position: absolute;
  width: 100%;  /* Initialement, le logo prend toute la largeur */
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 100000001;
`;

const Logo = ({ animate }) => {
  const logoRef = useRef(null);

  useEffect(() => {
    if (animate) {
      gsap.to(logoRef.current, {
        duration: 1,
        width: '200px',
        top: '50px',
        left: '50%',
        transform: 'translateX(-50%)',
        ease: 'power2.inOut'
      });
    }
  }, [animate]);

  return <LogoContainer ref={logoRef} src={LogoImage} alt="Logo" />;
};

export default Logo;
