import React, { useEffect, useState, useContext } from 'react';
import styled from 'styled-components';
import { UserContext } from './UserContext'; // Import the context

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: black;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: white;
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;
  width: fit-content;

  &:hover {
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const ButtonContainer = styled.div`
  margin-top: 16px;
  display: flex;
  flex-direction: column;
  align-items: end;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 40px;
  right: 40px;
  background: none;
  border: none;
  font-size: 40px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 46px;
  font-weight: bold;
  color: #000;
  margin-bottom: 32px;
  margin-right: 64px;
  line-height: 50px;
`;

const SubTitle = styled.h3`
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 16px;
`;

const ToyGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const ToyButton = styled.button`
  background: ${props => props.isSelected ? '#ff4500' : '#fff'};
  border: 1px solid ${props => props.isSelected ? '#ff4500' : '#ddd'};
  color: ${props => props.isSelected ? '#fff' : '#000'};
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;

  &:hover {
    background: #ff4500;
    color: #fff;
    border-color: #ff4500;
  }
`;

const CategoryButton = styled.button`
  background: ${props => props.isActive ? '#ff4500' : '#fff'};
  border: 1px solid ${props => props.isActive ? '#ff4500' : '#ddd'};
  color: ${props => props.isActive ? '#fff' : '#000'};
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;

  &:hover {
    background: #ff4500;
    color: #fff;
    border-color: #ff4500;
  }
`;

const CategoryGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 16px;
`;

const ColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const SmallColumnContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0px;
`;

const InputField = styled.input`
  padding: 10px;
  margin-bottom: 8px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Modal = ({ isOpen, onClose, onSave }) => {
  const { firstName1, firstName2, selectedToys, updateUserPreferences } = useContext(UserContext); // Use context

  // Step 1: Initialize the form state from localStorage or context
  const [formState, setFormState] = useState({
    firstName1: localStorage.getItem('firstName1') || firstName1 || '',
    firstName2: localStorage.getItem('firstName2') || firstName2 || '',
    selectedToys: selectedToys || [],
    selectedCategories: [], // Updated to handle multiple selected categories
  });

  const [toys, setToys] = useState([]);
  const [categories, setCategories] = useState(['accessoires', 'massage', 'bdsm']); // Example categories

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await fetch('http://localhost:1812/api/toys');
        const data = await response.json();
        setToys(data);
        console.log("Modal - Fetched toys:", data);
      } catch (error) {
        console.error('Error fetching toys:', error);
      }
    };

    fetchToys();
  }, []);

  // Step 2: Save formState to localStorage when names change
  useEffect(() => {
    localStorage.setItem('firstName1', formState.firstName1);
    localStorage.setItem('firstName2', formState.firstName2);
  }, [formState.firstName1, formState.firstName2]);

  const handleChange = (e) => {
    setFormState({
      ...formState,
      [e.target.name]: e.target.value,
    });
  };

  const toggleToySelection = (toy) => {
    setFormState((prev) => ({
      ...prev,
      selectedToys: prev.selectedToys.includes(toy)
        ? prev.selectedToys.filter(t => t !== toy)
        : [...prev.selectedToys, toy],
    }));
  };

  const toggleCategorySelection = (category) => {
    setFormState((prev) => ({
      ...prev,
      selectedCategories: prev.selectedCategories.includes(category)
        ? prev.selectedCategories.filter(c => c !== category)
        : [...prev.selectedCategories, category],
    }));
  };

  const handleSave = () => {
    updateUserPreferences(formState); // Update via context
    console.log("Modal - Saving data to context and localStorage:", formState);
    onSave(formState);
    onClose();
  };

  // Group toys by category
  const groupedToys = formState.selectedCategories.reduce((acc, category) => {
    acc[category] = toys.filter(toy => toy.category === category);
    return acc;
  }, {});

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Personnaliser votre expérience</SectionTitle>
        <ColumnContainer>
          <SmallColumnContainer>
            <SubTitle>À qui avons nous l'honneur ?</SubTitle>
            <InputField
              type="text"
              name="firstName1"
              placeholder="Femme"
              value={formState.firstName1}
              onChange={handleChange}
            />
            <InputField
              type="text"
              name="firstName2"
              placeholder="Homme"
              value={formState.firstName2}
              onChange={handleChange}
            />
          </SmallColumnContainer>
          <SmallColumnContainer>
            <SubTitle>Qu'est ce qu'il y'a au menu ?</SubTitle>
            <CategoryGrid>
              {categories.map((category, index) => (
                <CategoryButton
                  key={index}
                  isActive={formState.selectedCategories.includes(category)}
                  onClick={() => toggleCategorySelection(category)}
                >
                  {category}
                </CategoryButton>
              ))}
            </CategoryGrid>

            {/* Display toys grouped by category */}
            {Object.keys(groupedToys).map((category, index) => (
              <div key={index}>
                <SubTitle>{category}</SubTitle>
                <ToyGrid>
                  {groupedToys[category].map((toy, toyIndex) => (
                    <ToyButton
                      key={toyIndex}
                      isSelected={formState.selectedToys.includes(toy.name)}
                      onClick={() => toggleToySelection(toy.name)}
                    >
                      {toy.name}
                    </ToyButton>
                  ))}
                </ToyGrid>
              </div>
            ))}
          </SmallColumnContainer>
        </ColumnContainer>

        <ButtonContainer>
          <Button onClick={handleSave}>
            Confirmer 
          </Button>
        </ButtonContainer>
      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
